import React, { useEffect, useState } from 'react';

import { supabase } from '@/lib/supabaseClient';
import { useGo } from '@/utils/navigate';
import { normalizeSafeRedirect } from '@/utils/safeRedirect';

export type AuthFormMode = 'signin' | 'signup';

/**
 * Maps raw Supabase error messages to user-friendly descriptions
 */
function getAuthErrorMessage(
  rawMessage: string,
  mode: AuthFormMode,
  tab: 'password' | 'magic',
): string {
  const lower = rawMessage.toLowerCase();

  // Invalid credentials
  if (lower.includes('invalid') && (lower.includes('credentials') || lower.includes('login'))) {
    return 'The email or password you entered is incorrect. Please try again.';
  }

  // User not found
  if (lower.includes('user') && lower.includes('not found')) {
    return mode === 'signin'
      ? "We couldn't find an account with that email. Please check your email or sign up."
      : 'Unable to create account. Please try again.';
  }

  // Email already registered
  if (lower.includes('already') && lower.includes('registered')) {
    return 'An account with this email already exists. Try signing in instead.';
  }

  // Invalid email format
  if (lower.includes('invalid') && lower.includes('email')) {
    return 'Please enter a valid email address (e.g., you@example.com).';
  }

  // Password too weak
  if (lower.includes('password') && (lower.includes('weak') || lower.includes('strength'))) {
    return 'Please choose a stronger password with at least 8 characters, including letters and numbers.';
  }

  // Rate limiting
  if (lower.includes('rate limit') || lower.includes('too many')) {
    return 'Too many attempts. Please wait a few minutes before trying again.';
  }

  // Email sending issues
  if (lower.includes('email') && (lower.includes('send') || lower.includes('deliver'))) {
    return 'Unable to send email. Please check your email address and try again.';
  }

  // Network issues
  if (lower.includes('network') || lower.includes('connection') || lower.includes('timeout')) {
    return 'Connection issue. Please check your internet connection and try again.';
  }

  // Magic link specific
  if (tab === 'magic' && lower.includes('otp')) {
    return 'Unable to send magic link. Please try again or use email & password sign in.';
  }

  // Signup specific - email confirmation
  if (mode === 'signup' && lower.includes('confirm')) {
    return 'Please check your email to confirm your account before signing in.';
  }

  // Generic fallback with more helpful context
  if (rawMessage) {
    return `Authentication error: ${rawMessage}. Please try again or contact support if the issue persists.`;
  }

  return 'Something went wrong. Please try again or contact support if the issue persists.';
}

export function AuthForm({
  mode,
  redirect,
  primaryCtaLabel,
}: {
  mode: AuthFormMode;
  redirect: string;
  primaryCtaLabel: string;
}) {
  const go = useGo();
  const [activeTab, setActiveTab] = useState<'password' | 'magic'>('password');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');
  const [fullName, setFullName] = useState('');
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState<string | null>(null);
  const [notice, setNotice] = useState<string | null>(null);

  // Default tab is password on every mount and route
  useEffect(() => {
    setActiveTab('password');
  }, [mode]);

  // Prepare the callback URL for auth redirects - ensure we use the consistent naming for params
  const emailRedirectTo = `${window.location.origin}/auth/callback?next=${encodeURIComponent(redirect)}`;

  // Mode-specific validation and submission logic
  const handleSignIn = async () => {
    if (activeTab === 'password') {
      // Sign in with email/password
      const { error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) throw error;
      go(normalizeSafeRedirect(redirect));
      return;
    } else {
      // Magic Link flow (only available for sign-in)
      const { error } = await supabase.auth.signInWithOtp({
        email,
        options: {
          emailRedirectTo,
        },
      });

      if (error) throw error;
      setNotice('Magic link sent. Check your email.');
      // Optional: redirect to a "check email" page instead
      // go('/check-email?mode=signin', { replace: true });
    }
  };

  const handleSignUp = async () => {
    // Sign up requires email/password validation
    if (!password || password.length < 8) {
      setErr('Use at least 8 characters.');
      return false;
    }

    if (password !== confirm) {
      setErr('Passwords do not match.');
      return false;
    }

    // Create new account with optional full name
    const { error } = await supabase.auth.signUp({
      email,
      password,
      options: {
        emailRedirectTo,
        data: { full_name: fullName || undefined },
      },
    });

    if (error) throw error;

    // If using email confirmation (recommended), show a message
    setNotice('Check your email to confirm your account.');
    // Optional: redirect to a "check email" page instead
    // go('/check-email?mode=signup', { replace: true });

    return true;
  };

  const onSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setErr(null);
    setNotice(null);
    setLoading(true);

    try {
      if (mode === 'signup') {
        await handleSignUp();
      } else {
        await handleSignIn();
      }
    } catch (e: any) {
      // Map Supabase errors to user-friendly messages
      const errorMessage = getAuthErrorMessage(e?.message || '', mode, activeTab);
      setErr(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const showConfirm = mode === 'signup' && activeTab === 'password';

  // Mode-specific UI helpers
  const tabBtn = (k: 'password' | 'magic', label: string) => (
    <button
      type="button"
      onClick={() => setActiveTab(k)}
      className={`px-3 py-2 text-sm font-medium ${activeTab === k ? 'text-[#13294B] border-b-2 border-[#D4AF37]' : 'text-slate-500'}`}
      aria-pressed={activeTab === k}
    >
      {label}
    </button>
  );

  // Mode-specific labels and hints
  const emailPlaceholder = mode === 'signin' ? 'Enter your email' : 'Enter your email address';
  const passwordPlaceholder =
    mode === 'signin' ? 'Enter your password' : 'Create a strong password';
  const passwordLabel = mode === 'signin' ? 'Password' : 'Create password';

  return (
    <form onSubmit={onSubmit} className="space-y-5">
      {/* Tab selector - only show Magic Link option for sign-in */}
      <div className="flex items-center gap-4 border-b pb-2">
        {tabBtn('password', 'Email & Password')}
        {mode === 'signin' && (
          <>
            <span className="h-5 w-px bg-slate-200" />
            {tabBtn('magic', 'Magic Link')}
          </>
        )}
      </div>

      <div className="space-y-4">
        {/* Full Name field - only for sign-up */}
        {mode === 'signup' && (
          <div>
            <label htmlFor="fullname-input" className="block text-sm font-medium text-slate-700">
              Full name
            </label>
            <input
              id="fullname-input"
              type="text"
              value={fullName}
              onChange={(e) => setFullName(e.target.value)}
              className="mt-1 w-full rounded-lg border border-slate-300 px-3 py-2 outline-none focus:ring-2 focus:ring-[#D4AF37]"
              autoComplete="name"
              placeholder="Enter your full name"
            />
          </div>
        )}

        {/* Email field - common to both modes */}
        <div>
          <label htmlFor="email-input" className="block text-sm font-medium text-slate-700">
            Email address
          </label>
          <input
            id="email-input"
            required
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="mt-1 w-full rounded-lg border border-slate-300 px-3 py-2 outline-none focus:ring-2 focus:ring-[#D4AF37]"
            autoComplete="email"
            placeholder={emailPlaceholder}
          />
        </div>

        {activeTab === 'password' && (
          <>
            <div>
              <label htmlFor="password-input" className="block text-sm font-medium text-slate-700">
                {passwordLabel}
              </label>
              <input
                id="password-input"
                required
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="mt-1 w-full rounded-lg border border-slate-300 px-3 py-2 outline-none focus:ring-2 focus:ring-[#D4AF37]"
                autoComplete={mode === 'signup' ? 'new-password' : 'current-password'}
                placeholder={passwordPlaceholder}
                minLength={mode === 'signup' ? 8 : undefined}
              />
              {mode === 'signin' && (
                <div className="mt-2 text-right">
                  <a
                    href="/auth/forgot-password"
                    className="text-sm text-[#13294B] hover:underline"
                  >
                    Forgot your password?
                  </a>
                </div>
              )}
            </div>

            {showConfirm && (
              <div>
                <label
                  htmlFor="confirm-password-input"
                  className="block text-sm font-medium text-slate-700"
                >
                  Confirm password
                </label>
                <input
                  id="confirm-password-input"
                  required
                  type="password"
                  value={confirm}
                  onChange={(e) => setConfirm(e.target.value)}
                  className="mt-1 w-full rounded-lg border border-slate-300 px-3 py-2 outline-none focus:ring-2 focus:ring-[#D4AF37]"
                  autoComplete="new-password"
                  placeholder="Confirm your password"
                />
              </div>
            )}
          </>
        )}
      </div>

      {/* Error and notification handling */}
      {err && <p className="text-sm text-red-600">{err}</p>}
      {notice && <p className="text-sm text-emerald-700">{notice}</p>}

      {/* Mode-specific submission button */}
      <button
        type="submit"
        disabled={loading}
        className={`w-full rounded-xl bg-[#13294B] text-white px-4 py-3 font-semibold ring-1 ring-black/5 hover:opacity-90 disabled:opacity-60`}
        data-testid={`${mode}-button`}
      >
        {loading
          ? 'Please wait‚Ä¶'
          : mode === 'signup'
            ? 'Create account'
            : activeTab === 'password'
              ? 'Sign in'
              : 'Send magic link'}
      </button>

      {/* Mode-specific helper text */}
      {mode === 'signin' && activeTab === 'password' && (
        <p className="text-center text-xs text-slate-500">
          Trouble signing in? Try the Magic Link tab for password-free sign in.
        </p>
      )}
      {mode === 'signin' && activeTab === 'magic' && (
        <p className="text-center text-xs text-slate-500">
          We'll email you a secure link for password-free access to your account.
        </p>
      )}
      {mode === 'signup' && (
        <p className="text-center text-xs text-slate-500">
          By creating an account, you agree to our Terms of Service and Privacy Policy.
        </p>
      )}

      {/* Account switching links removed - now handled by AuthFooter component */}
    </form>
  );
}

// src/components/Dashboard/EnhancedDashboard.tsx
import { PlusCircle, FileText, BarChart3, Target, Zap, Star, ArrowRight, X } from 'lucide-react';
import React, { useState, useEffect } from 'react';

import { InkwellFeather } from '@/components/icons';
import type { InkwellIconName } from '@/components/icons/InkwellFeather';
import StatusChip from '@/components/Storage/StatusChip';
import { StorageHealthWidget } from '@/components/Storage/StorageHealthWidget';
import { useAppContext, View } from '@/context/AppContext';
import { useTourStartupFromUrl } from '@/hooks/useTourStartupFromUrl';
import { useUI } from '@/hooks/useUI';
import { triggerOnProjectCreated } from '@/utils/tourTriggers';

const EnhancedDashboard: React.FC = () => {
  const { state, currentProject, addProject, setCurrentProjectId, dispatch } = useAppContext();
  const { newProjectDialogOpen, openNewProjectDialog, closeNewProjectDialog } = useUI();
  const [storageModalOpen, setStorageModalOpen] = useState(false);

  // Check for tour=start in URL and trigger tour if found
  useTourStartupFromUrl();

  // Handle URL parameter and keyboard shortcut for storage modal
  useEffect(() => {
    // Check for ?storage=1 in URL
    if (new URLSearchParams(window.location.search).get('storage') === '1') {
      setStorageModalOpen(true);
    }

    // Keyboard shortcut: Cmd/Ctrl + Shift + S
    const onKey = (e: KeyboardEvent) => {
      if ((e.ctrlKey || e.metaKey) && e.shiftKey && e.key.toLowerCase() === 's') {
        e.preventDefault();
        setStorageModalOpen((v) => !v);
      }
    };
    window.addEventListener('keydown', onKey);
    return () => window.removeEventListener('keydown', onKey);
  }, []);

  const _createNewProject = async () => {
    try {
      const newProject = {
        id: `project-${Date.now()}`,
        name: `New Story ${state.projects.length + 1}`,
        description: 'A new fiction project',
        content: '',
        createdAt: Date.now(),
        updatedAt: Date.now(),
      };

      addProject({ ...newProject, chapters: [], characters: [], beatSheet: [] });
      setCurrentProjectId(newProject.id);

      // Fire tour trigger for project creation
      triggerOnProjectCreated(newProject.id);

      // Auto-navigate to writing after creation
      setTimeout(() => {
        dispatch({ type: 'SET_VIEW', payload: View.Writing });
      }, 500);
    } catch (error) {
      console.error('Failed to create project:', error);
    }
  };

  const navigateToView = (view: View) => {
    dispatch({ type: 'SET_VIEW', payload: view });
  };

  const formatDate = (timestamp: number) => {
    return new Date(timestamp).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
    });
  };

  const getProjectWordCount = (project: any) => {
    if (!project.content) return 0;
    return project.content.split(' ').filter((word: string) => word.length > 0).length;
  };

  const getDaysAgo = (timestamp: number) => {
    const days = Math.floor((Date.now() - timestamp) / (1000 * 60 * 60 * 24));
    if (days === 0) return 'Today';
    if (days === 1) return '1 day ago';
    return `${days} days ago`;
  };

  const quickActions = [
    {
      id: 'new-project',
      title: 'Create New Project',
      description: 'Start a fresh writing project with templates and structure',
      icon: PlusCircle,
      color: 'bg-blue-50 dark:bg-blue-900/20 text-blue-600 dark:text-blue-400',
      action: () => openNewProjectDialog(),
    },
    {
      id: 'continue-writing',
      title: 'Continue Writing',
      description: 'Pick up where you left off in your current project',
      icon: FileText,
      color: 'bg-green-50 dark:bg-green-900/20 text-green-600 dark:text-green-400',
      action: () => navigateToView(View.Writing),
      disabled: !currentProject,
    },
    {
      id: 'view-analytics',
      title: 'View Analytics',
      description: 'Check your writing progress and statistics',
      icon: BarChart3,
      color: 'bg-purple-50 dark:bg-purple-900/20 text-purple-600 dark:text-purple-400',
      action: () => navigateToView(View.Analysis),
    },
    {
      id: 'plan-story',
      title: 'Plan Your Story',
      description: 'Organize characters, plots, and story structure',
      icon: () => <InkwellFeather name={'planning' as InkwellIconName} size="sm" />,
      color: 'bg-orange-50 dark:bg-orange-900/20 text-orange-600 dark:text-orange-400',
      action: () => navigateToView(View.Planning),
    },
  ];

  // If no projects exist, show onboarding
  if (state.projects.length === 0) {
    return (
      <div className="enhanced-dashboard fade-in">
        <div className="max-w-4xl mx-auto text-center py-16">
          {/* Welcome Header */}
          <div className="mb-12">
            <div className="w-20 h-20 bg-primary-100 dark:bg-primary-900/30 rounded-full flex items-center justify-center mx-auto mb-6">
              <InkwellFeather name="writing" size="2xl" color="primary" />
            </div>
            <h1 className="text-4xl font-bold text-slate-900 dark:text-white mb-4">
              Welcome to Inkwell
            </h1>
            <p className="text-lg text-slate-600 dark:text-slate-300 max-w-2xl mx-auto">
              Your professional writing companion. Create compelling stories, track your progress,
              and achieve your writing goals with powerful tools designed for serious writers.
            </p>
          </div>

          {/* Getting Started */}
          <div className="bg-white dark:bg-slate-800 rounded-xl shadow-sm border border-slate-200 dark:border-slate-700 p-8 mb-8">
            <h2 className="text-2xl font-semibold text-slate-900 dark:text-white mb-6">
              Let's get you started
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
              <div className="text-center p-4">
                <div className="w-12 h-12 bg-blue-100 dark:bg-blue-900/30 rounded-lg flex items-center justify-center mx-auto mb-3">
                  <span className="text-xl">üìù</span>
                </div>
                <h3 className="text-lg font-semibold text-slate-900 dark:text-white mb-2">
                  Create
                </h3>
                <p className="text-sm text-slate-600 dark:text-slate-400">
                  Start a new project with our guided setup
                </p>
              </div>
              <div className="text-center p-4">
                <div className="w-12 h-12 bg-green-100 dark:bg-green-900/30 rounded-lg flex items-center justify-center mx-auto mb-3">
                  <span className="text-xl">‚úçÔ∏è</span>
                </div>
                <h3 className="text-lg font-semibold text-slate-900 dark:text-white mb-2">Write</h3>
                <p className="text-sm text-slate-600 dark:text-slate-400">
                  Focus on your story with our distraction-free editor
                </p>
              </div>
              <div className="text-center p-4">
                <div className="w-12 h-12 bg-purple-100 dark:bg-purple-900/30 rounded-lg flex items-center justify-center mx-auto mb-3">
                  <span className="text-xl">üìä</span>
                </div>
                <h3 className="text-lg font-semibold text-slate-900 dark:text-white mb-2">Track</h3>
                <p className="text-sm text-slate-600 dark:text-slate-400">
                  Monitor progress and celebrate milestones
                </p>
              </div>
            </div>
            <button
              onClick={openNewProjectDialog}
              className="btn btn-primary btn-lg"
              type="button"
              data-test="create-first-project"
              data-tour-id="create-project-button"
            >
              <PlusCircle className="w-5 h-5" />
              Create Your First Project
            </button>
          </div>

          {/* Features Preview */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 text-left">
            <div className="card p-6">
              <div className="flex items-start gap-4">
                <div className="w-10 h-10 bg-blue-100 dark:bg-blue-900/30 rounded-lg flex items-center justify-center flex-shrink-0">
                  <Target className="w-5 h-5 text-blue-600 dark:text-blue-400" />
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-slate-900 dark:text-white mb-2">
                    Goal Tracking
                  </h3>
                  <p className="text-sm text-slate-600 dark:text-slate-400">
                    Set daily word count goals and track your writing streaks
                  </p>
                </div>
              </div>
            </div>
            <div className="card p-6">
              <div className="flex items-start gap-4">
                <div className="w-10 h-10 bg-green-100 dark:bg-green-900/30 rounded-lg flex items-center justify-center flex-shrink-0">
                  <Zap className="w-5 h-5 text-green-600 dark:text-green-400" />
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-slate-900 dark:text-white mb-2">
                    Smart Analytics
                  </h3>
                  <p className="text-sm text-slate-600 dark:text-slate-400">
                    Get insights into your writing patterns and productivity
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="enhanced-dashboard fade-in">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center justify-between mb-2">
          <h1 className="text-4xl font-bold text-slate-900 dark:text-white">Dashboard</h1>
          <div className="flex items-center gap-3">
            <StatusChip onClick={() => setStorageModalOpen(true)} />
            <button
              onClick={openNewProjectDialog}
              className="btn btn-primary"
              type="button"
              data-test="new-project"
              data-tour-id="new-project-button"
            >
              <PlusCircle className="w-4 h-4" />
              New Project
            </button>
          </div>
        </div>
        <p className="text-base text-slate-600 dark:text-slate-400">
          Welcome back! Here's your writing overview and recent activity.
        </p>
      </div>

      {/* Storage Health Modal */}
      {storageModalOpen && (
        <div
          role="dialog"
          aria-modal="true"
          className="fixed inset-0 z-50 flex items-center justify-center p-4"
        >
          <div
            className="absolute inset-0 bg-black/40 backdrop-blur-sm"
            onClick={() => setStorageModalOpen(false)}
          />
          <div className="relative z-10 w-[680px] max-w-[92vw] rounded-2xl border border-slate-200 bg-white p-6 shadow-2xl dark:border-slate-700 dark:bg-slate-900">
            <div className="mb-4 flex items-center justify-between">
              <h2 className="text-2xl font-semibold text-slate-900 dark:text-white">
                Storage Health
              </h2>
              <button
                className="rounded-lg border border-slate-300 bg-white px-3 py-1.5 text-sm font-medium text-slate-700 transition-colors hover:bg-slate-50 dark:border-slate-600 dark:bg-slate-800 dark:text-slate-300 dark:hover:bg-slate-700"
                onClick={() => setStorageModalOpen(false)}
                aria-label="Close storage health modal"
              >
                <X className="h-4 w-4" />
              </button>
            </div>
            <p className="mb-6 text-sm text-slate-600 dark:text-slate-400">
              Monitor your browser storage status, persistence, and quota usage. Press{' '}
              <kbd className="rounded bg-slate-100 px-1.5 py-0.5 text-xs font-medium dark:bg-slate-800">
                Cmd/Ctrl+Shift+S
              </kbd>{' '}
              to toggle this panel.
            </p>

            <StorageHealthWidget />
          </div>
        </div>
      )}

      {/* Current Project Highlight */}
      {currentProject && (
        <div className="card card-interactive mb-8" onClick={() => navigateToView(View.Writing)}>
          <div className="card-content">
            <div className="flex items-start justify-between mb-4">
              <div className="flex items-start gap-4">
                <div className="w-12 h-12 bg-primary-100 dark:bg-primary-900/30 rounded-lg flex items-center justify-center flex-shrink-0">
                  <InkwellFeather name="writing" size="lg" color="primary" />
                </div>
                <div>
                  <h2 className="text-2xl font-semibold text-slate-900 dark:text-white mb-1">
                    {currentProject.name}
                  </h2>
                  <p className="text-sm text-slate-600 dark:text-slate-400 mb-2">
                    {currentProject.description}
                  </p>
                  <div className="flex items-center gap-4 text-xs text-slate-500">
                    <span>Last updated {getDaysAgo(currentProject.updatedAt)}</span>
                    <span>‚Ä¢</span>
                    <span>{getProjectWordCount(currentProject)} words</span>
                  </div>
                </div>
              </div>
              <div className="flex items-center gap-2 text-primary-600 dark:text-primary-400">
                <span className="text-sm font-medium">Continue writing</span>
                <ArrowRight className="w-4 h-4" />
              </div>
            </div>

            {/* Project Stats */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 pt-4 border-t border-slate-200 dark:border-slate-700">
              <div className="text-center">
                <div className="text-xl font-semibold text-slate-900 dark:text-white mb-1">
                  {getProjectWordCount(currentProject).toLocaleString()}
                </div>
                <div className="text-xs text-slate-500">Total Words</div>
              </div>
              <div className="text-center">
                <div className="text-xl font-semibold text-slate-900 dark:text-white mb-1">
                  {currentProject.chapters?.length || 0}
                </div>
                <div className="text-xs text-slate-500">Chapters</div>
              </div>
              <div className="text-center">
                <div className="text-xl font-semibold text-slate-900 dark:text-white mb-1">
                  {currentProject.characters?.length || 0}
                </div>
                <div className="text-xs text-slate-500">Characters</div>
              </div>
              <div className="text-center">
                <div className="text-xl font-semibold text-green-600 dark:text-green-400 mb-1">
                  85%
                </div>
