  if (!globalThis.__inkwell_has_global_exportformat__) {
    type ExportFormat = 'markdown' | 'html' | 'pdf' | 'docx' | (string & {});
    // @ts-ignore
    globalThis.__inkwell_has_global_exportformat__ = true;
  }
}

