import devLog from "src/utils/devLogger";
import React, { useEffect, useState } from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';

import { log } from '@/utils/logger';

// UI + panels
import ClaudeAssistant from './components/ClaudeAssistant';
import ClaudeErrorBoundary from './components/ClaudeErrorBoundary';
import CommandPaletteUI from './components/CommandPalette/CommandPaletteUI';
import DebugSearchPanel from './components/DebugSearchPanel';
import { AppErrorBoundary } from './components/ErrorBoundary';
import { ExportWizardModal } from './components/ExportWizard/ExportWizardModal';
import HealthCheck from './components/HealthCheck';
import MainLayout from './components/Layout/MainLayout';
import { OnboardingUI } from './components/Onboarding/OnboardingUI';
import { PWAInstallButton, PWAUpdateNotification } from './components/PWA';
import {
  StorageRecoveryBanner,
  OfflineBanner,
  useStorageRecovery,
} from './components/Recovery/StorageRecoveryBanner';
import { PreviewGuard } from './components/RouteGuards/PreviewGuard';
import { StorageBanner } from './components/Storage/StorageBanner';
import { ToastContainer } from './components/ToastContainer';
import ViewSwitcher from './components/ViewSwitcher';
// Context and providers
import { useAppContext } from './context/AppContext';
import { useAuth } from './context/AuthContext';
import { useEditorContext } from './context/EditorContext';
// Route guards
// Tutorial Router
// Pages
import PreviewDashboard from './features/preview/PreviewDashboard';
import PreviewLandingPage from './features/preview/PreviewLandingPage';
import PreviewWriter from './features/preview/PreviewWriter';
import { usePrivateModeWarning } from './hooks/usePrivateModeWarning';
import { OnboardingOrchestrator } from './onboarding/OnboardingOrchestrator';
import AuthCallback from './pages/AuthCallback';
import BrandPage from './pages/Brand';
import ForgotPassword from './pages/ForgotPassword';
import SignIn from './pages/SignInPage';
import SignUp from './pages/SignUpPage';
import UpdatePassword from './pages/UpdatePassword';
import AnonOnlyRoute from './routes/AnonOnlyRoute';
import SupabaseHealth from './routes/Health';
import ProtectedRoute from './routes/ProtectedRoute';
// Services
import { connectivityService } from './services/connectivityService';
import { enhancedStorageService } from './services/enhancedStorageService';
import { useTourRouterAdapter } from './tour/adapters/routerAdapter';
import { TourLifecycleIntegration } from './tour/integrations/tourLifecycleIntegration';
import { SpotlightOverlay } from './tour/ui';
import { useTourRegistration } from './tour/useTourRegistration';
import { isPublicRoute } from './utils/auth';
// Tour components

// Debug utilities (development only)
if (import.meta.env.DEV) {
  import('./utils/debugOnboardingGate');
}

// Types for connectivity status
interface ConnectivityStatus {
  isOnline: boolean;
  queuedWrites: number;
}

type QueuedOperation = {
  id: string | number;
  operation: string;
  timestamp: number;
  retryCount?: number;
};

// RootRedirect component to intelligently handle the root route based on auth state
function RootRedirect() {
  const { user, loading } = useAuth();
  // Use state to track if we've already initiated a redirect
  const [hasInitiatedRedirect, setHasInitiatedRedirect] = useState(false);

  // Only redirect once auth state is fully loaded and we haven't already redirected
  useEffect(() => {
    if (!loading && !hasInitiatedRedirect) {
      setHasInitiatedRedirect(true);
      devLog.debug(
        '[RootRedirect] Auth loaded, user state:',
        user ? 'authenticated' : 'unauthenticated',
      );
    }
  }, [loading, user, hasInitiatedRedirect]);

  if (loading || !hasInitiatedRedirect) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900">
        <div className="text-center">
          <div className="w-12 h-12 border-4 border-inkwell-navy border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">Loading...</p>
        </div>
      </div>
    );
  }

  // If user is authenticated, go to dashboard
  // If not authenticated, go to sign-in page (without any confusing query parameters)
  return user ? <Navigate to="/dashboard" replace /> : <Navigate to="/sign-in" replace />;
}

// All app logic lives here, safely *inside* the providers.
function AppShell() {
  const location = useLocation();
  const { user, loading, signOut } = useAuth();
  const isPublic = isPublicRoute(location.pathname);

  // Register tours on app boot
  useTourRegistration();

  // Enable tour router adapter (refreshes anchors on route changes)
  useTourRouterAdapter();

  // Show loading spinner while checking auth (prevents content flash)
  if (loading) {
    // Log loading state for debugging
    devLog.debug('[App] Auth loading state active, showing loading spinner');

    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900">
        <div className="text-center">
          <div className="w-12 h-12 border-4 border-inkwell-navy border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">Loading authentication...</p>
          <p className="text-xs text-gray-500 mt-2">
            If this persists, please check console for errors.
          </p>
          <p className="text-xs text-gray-500">
            Inkwell v{import.meta.env.VITE_APP_VERSION || '1.0.2'}
          </p>
        </div>
      </div>
    );
  }

  // Redirect to sign-in if not on public route and not signed in
  if (!isPublic && !user) {
    return <Navigate to="/sign-in" replace />;
  }

  return (
    <>
      {/* Tour lifecycle integration - wires analytics and persistence */}
      <TourLifecycleIntegration />

      {/* Spotlight overlay for tour UI */}
      <SpotlightOverlay />

      {/* New unified onboarding UI (welcome modal, checklist, contextual hints) */}
      <OnboardingUI />

      <header className="flex justify-between items-center p-4 border-b">
        <div className="flex items-center gap-4">
          {!user ? (
            <a href="/sign-in" className="text-blue-600 hover:text-blue-700">
              Sign in
            </a>
          ) : (
            <button onClick={signOut} className="text-sm text-gray-600 hover:text-gray-900">
              Sign out
            </button>
          )}
        </div>
      </header>
      <Routes>
        {/* Health check route */}
        <Route path="/health" element={<HealthCheck />} />

        {/* Supabase integration health check */}
        <Route path="/health/supabase" element={<SupabaseHealth />} />

        {/* Auth callback route - handles magic link code exchange and password reset flow */}
        <Route path="/auth/callback" element={<AuthCallback />} />

        {/* Auth routes - protected from authenticated users */}
        <Route
          path="/sign-in"
          element={
            <AnonOnlyRoute>
              <SignIn />
            </AnonOnlyRoute>
          }
        />
        <Route
          path="/sign-up"
          element={
            <AnonOnlyRoute>
              <SignUp />
            </AnonOnlyRoute>
          }
        />
        <Route path="/auth/forgot-password" element={<ForgotPassword />} />
        <Route path="/auth/update-password" element={<UpdatePassword />} />

        {/* Legacy routes - redirects */}
        <Route path="/login" element={<Navigate to="/sign-in" replace />} />
        <Route path="/signup" element={<Navigate to="/sign-up" replace />} />

        {/* Preview mode routes - for unauthenticated users */}
        <Route
          path="/preview"
          element={
            <PreviewGuard>
              <PreviewLandingPage />
            </PreviewGuard>
          }
        />
        <Route
          path="/preview/write"
          element={
            <PreviewGuard>
              <PreviewWriter />
            </PreviewGuard>
          }
        />
        <Route
          path="/preview/dashboard"
          element={
            <PreviewGuard>
              <PreviewDashboard />
            </PreviewGuard>
          }
        />

        {/* Root redirect */}
        <Route path="/" element={<RootRedirect />} />

        {/* Dashboard route - main app */}
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <ProfileAppShell />
            </ProtectedRoute>
          }
        />

        {/* Brand showcase route */}
        <Route
          path="/brand"
          element={
            <ProtectedRoute>
              <BrandPage />
            </ProtectedRoute>
          }
        />

        {/* Not Found - explicit redirect to root */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </>
  );
}

// Profile-specific app shell (the original app logic)
function ProfileAppShell() {
  const { claude, currentProject } = useAppContext();
  const { insertText } = useEditorContext();

  // storage recovery
  const { showRecoveryBanner, dismissRecoveryBanner } = useStorageRecovery();

  // Private mode warning - warns before closing if in private mode
  usePrivateModeWarning(false); // Can be enhanced to track actual unsaved changes

  // connectivity
  const [connectivityStatus, setConnectivityStatus] = useState<ConnectivityStatus>({
    isOnline: true,
    queuedWrites: 0,
  });

  useEffect(() => {
    const unsubscribe = connectivityService.onStatusChange((status) => {
      setConnectivityStatus({
        isOnline: status.isOnline,
        queuedWrites: status.queuedWrites,
      });
    });

    const initialStatus = connectivityService.getStatus();
    setConnectivityStatus({
      isOnline: initialStatus.isOnline,
      queuedWrites: initialStatus.queuedWrites,
    });

    return unsubscribe;
  }, []);

  // maintenance after start
  useEffect(() => {
    const performStartupMaintenance = async () => {
      try {
        const result = await enhancedStorageService.performMaintenance();
        if (result.actions.length > 0) {
          devLog.debug('Startup maintenance completed:', result.actions);
        }
      } catch (error) {
        log.warn('Startup maintenance failed:', error);
      }
    };

    const timeoutId = setTimeout(performStartupMaintenance, 2000);
    return () => clearTimeout(timeoutId);
  }, []);

  // export dialog state
  const [isExportDialogOpen, setIsExportDialogOpen] = useState(false);
  const openExportDialog = () => setIsExportDialogOpen(true);
  const closeExportDialog = () => setIsExportDialogOpen(false);

  // offline queue modal
  const [showOfflineQueue, setShowOfflineQueue] = useState(false);
  const handleViewQueue = () => setShowOfflineQueue(true);
  const handleDismissOfflineBanner = () => {
    // optional: persist snooze preference
  };

  return (
    <>
      {/* Stable tour anchor - always present for tour steps */}
      <div
        id="tour-viewport-anchor"
        style={{ position: 'fixed', inset: 0, pointerEvents: 'none' }}
      />

      {/* Storage Health Banner - shows warnings about private mode, non-persistent storage, etc */}
      <StorageBanner />

      {/* Storage Recovery Banner */}
      {showRecoveryBanner && <StorageRecoveryBanner onDismiss={dismissRecoveryBanner} />}

      {/* Offline Banner */}
      {!connectivityStatus.isOnline && connectivityStatus.queuedWrites > 0 && (
        <OfflineBanner
          queuedOperations={connectivityStatus.queuedWrites}
          onViewQueue={handleViewQueue}
          onDismiss={handleDismissOfflineBanner}
        />
      )}

      {/* PWA Update Notification */}
      <PWAUpdateNotification />

      {/* PWA Install Prompt */}
      <PWAInstallButton variant="fab" />

      <MainLayout>
        <ViewSwitcher />
        <ToastContainer />

        {/* Claude Assistant with Error Boundary */}
        {claude?.isVisible && (
          <ClaudeErrorBoundary>
            <ClaudeAssistant
              selectedText=""
              onInsertText={(text) => {
                insertText(text);
              }}
            />
          </ClaudeErrorBoundary>
        )}

        {/* Export Wizard */}
        {currentProject && (
          <ExportWizardModal
            isOpen={isExportDialogOpen}
            projectId={currentProject.id}
            onClose={closeExportDialog}
          />
        )}

        {/* Offline Queue Modal */}
        {showOfflineQueue && (
          <OfflineQueueModal isOpen={showOfflineQueue} onClose={() => setShowOfflineQueue(false)} />
        )}

        {/* Command Palette UI */}
        <CommandPaletteUI />

        {/* Onboarding System */}
        <OnboardingOrchestrator />

        {/* Hidden global export trigger */}
        <div style={{ display: 'none' }}>
          <button onClick={openExportDialog} id="global-export-trigger">
            Export
          </button>
        </div>

        {/* Dev-only debug panels */}
        {import.meta.env.DEV && <DebugSearchPanel />}
        {import.meta.env.DEV && <StorageDebugPanel />}
      </MainLayout>
    </>
  );
}

// Offline Queue Management Modal
function OfflineQueueModal(_props: OfflineQueueModalProps) {
  const { isOpen, onClose } = _props;
  const [queuedOperations, setQueuedOperations] = useState<QueuedOperation[]>([]);

  useEffect(() => {
    if (isOpen) {
      const operations = (connectivityService.getQueuedOperations?.() ?? []) as QueuedOperation[];
      setQueuedOperations(Array.isArray(operations) ? operations : []);
    }
  }, [isOpen]);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-semibold">Queued Operations</h3>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            Ã—
          </button>
        </div>
        <div className="space-y-2 max-h-60 overflow-y-auto">
          {queuedOperations.length === 0 ? (
            <p className="text-gray-500">No operations queued</p>
          ) : (
            queuedOperations.map((op) => (
              <div key={String(op.id)} className="p-2 bg-gray-100 rounded">
                <div className="text-sm font-medium">{op.operation}</div>
                <div className="text-xs text-gray-500">
                  {new Date(op.timestamp).toLocaleTimeString()}
                  {op.retryCount && op.retryCount > 0 ? ` (Retry ${op.retryCount})` : ''}
                </div>
              </div>
            ))
          )}
        </div>
        <div className="mt-4 flex justify-end gap-2">
          <button
            onClick={async () => {
              await connectivityService.clearQueue?.();
              setQueuedOperations([]);
            }}
            className="px-3 py-1 text-sm bg-red-600 text-white rounded hover:bg-red-700"
          >
            Clear Queue
          </button>
          <button
            onClick={onClose}
            className="px-3 py-1 text-sm bg-gray-600 text-white rounded hover:bg-gray-700"
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
}

// Development Storage Debug Panel
function StorageDebugPanel() {
  const [stats, setStats] = useState<any>(null);
  const [isExpanded, setIsExpanded] = useState(false);

  const refreshStats = async () => {
    try {
      const storageStats = await enhancedStorageService.getStorageStats();
      setStats(storageStats);
    } catch (error) {
      log.error('Failed to get storage stats:', error);
    }
  };

  useEffect(() => {
    refreshStats();
    const interval = setInterval(refreshStats, 5000);
    return () => clearInterval(interval);
  }, []);

  if (!stats) return null;

  const mb = (n: number) => (n > 0 ? (n / 1024 / 1024).toFixed(1) : '0.0');

  return (
    <div className="fixed bottom-4 right-4 z-40">
      <div className="bg-gray-900 text-white text-xs rounded-lg shadow-lg">
        <button
          onClick={() => setIsExpanded((v) => !v)}
          className="w-full px-3 py-2 text-left hover:bg-gray-800 rounded-lg"
        >
          Storage: {mb(stats.storageUsed)} MB
        </button>
        {isExpanded && (
          <div className="px-3 pb-3 space-y-1 border-t border-gray-700 pt-2">
            <div>Projects: {stats.totalProjects}</div>
            <div>Words: {stats.totalWordCount?.toLocaleString?.() ?? 'â€”'}</div>
            <div>Sessions: {stats.writingSessions}</div>
            <div>Snapshots: {stats.snapshotCount}</div>
            <div>
              Usage:{' '}
              {stats.quotaInfo
                ? `${((stats.quotaInfo.percentUsed ?? 0) * 100).toFixed(1)}%`
                : 'Unknown'}
            </div>
            <button
              onClick={async () => {
                const result = await enhancedStorageService.performMaintenance();
                devLog.debug('Manual maintenance:', result);
                refreshStats();
              }}
              className="w-full mt-2 px-2 py-1 bg-blue-600 hover:bg-blue-700 rounded text-xs"
            >
              Run Maintenance
            </button>
            <button
              onClick={async () => {
                if (
                  confirm('Reset all data and reload? This will clear IndexedDB and localStorage.')
                ) {
                  try {
                    // Clear localStorage
                    localStorage.clear();

                    // Clear IndexedDB
                    if ('indexedDB' in window) {
                      const databases = await indexedDB.databases();
                      await Promise.all(
                        databases.map((db) =>
                          db.name
                            ? new Promise<void>((resolve, reject) => {
                                const deleteReq = indexedDB.deleteDatabase(db.name!);
                                deleteReq.onsuccess = () => resolve();
                                deleteReq.onerror = () =>
                                  reject(deleteReq.error || new Error('Failed to delete database'));
                              })
                            : Promise.resolve(),
                        ),
                      );
                    }

                    // Reload the page
                    window.location.reload();
                  } catch (error) {
                    log.error('Failed to reset:', error);
                    alert('Reset failed. Check console for details.');
                  }
                }
              }}
              className="w-full mt-1 px-2 py-1 bg-red-600 hover:bg-red-700 rounded text-xs"
            >
              ðŸ”„ Reset & Reload
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

// Root export: use centralized Providers component for clean composition
// Interfaces
interface OfflineQueueModalProps {
  isOpen: boolean;
  onClose: () => void;
}

interface _ProfileAppShellProps {}

interface _StorageDebugPanelProps {}

interface _AppShellProps {}

export default function _App() {
  return (
    <AppErrorBoundary level="app">
      <AppShell />
    </AppErrorBoundary>
  );
}

// src/components/Analytics/WritingAnalyticsView.tsx
import { TrendingUp, Calendar, Target, Clock, BookOpen, Award, Edit3 } from 'lucide-react';
import React, { useMemo, useState } from 'react';

import PhraseHygieneWidget from '@/components/Analytics/PhraseHygieneWidget';
import PerformanceChart from '@/components/PerformanceChart';
import { useAppContext } from '@/context/AppContext';

type Session = {
  startTime?: string;
  wordCount?: number;
  wordsAdded?: number;
  focusTime?: number;
  dateLabel?: string;
  productivity?: number;
};

export default function WritingAnalyticsView() {
  const { currentProject } = useAppContext();
  const [timeRange, setTimeRange] = useState<'week' | 'month' | 'year'>('month');

  // Read from a defensively-typed object so we don't rely on Project having `sessions`
  const proj: any = currentProject ?? {};
  const sessions: Session[] = Array.isArray(proj.sessions) ? (proj.sessions as Session[]) : [];
  const projectName: string = typeof proj.name === 'string' ? proj.name : 'Project';

  // Daily trend rows for primary chart
  const trendRows = useMemo(() => {
    return sessions.map((s) => ({
      date: s?.dateLabel ?? (s?.startTime ? new Date(s.startTime).toLocaleDateString() : ''),
      words:
        typeof s?.wordCount === 'number'
          ? s.wordCount
          : typeof s?.wordsAdded === 'number'
            ? s.wordsAdded
            : 0,
    }));
  }, [sessions]);

  // Totals and summary metrics
  const totalWords = trendRows.reduce(
    (sum, d) => sum + (typeof d.words === 'number' ? d.words : 0),
    0,
  );
  const averageWordsPerDay = trendRows.length ? Math.round(totalWords / trendRows.length) : 0;

  const totalWritingTimeMin = sessions.reduce((sum, s) => sum + (s.focusTime ?? 0), 0);

  const writingStreak = _computeStreak(sessions);

  const todayWords = (() => {
    try {
      const todayStr = new Date().toISOString().slice(0, 10);
      let words = 0;
      for (const s of sessions) {
        if (!s.startTime) continue;
        const d = new Date(s.startTime).toISOString().slice(0, 10);
        if (d === todayStr) {
          words += s.wordsAdded ?? s.wordCount ?? 0;
        }
      }
      return words;
    } catch {
      return 0;
    }
  })();

  const dailyGoal = typeof proj.dailyGoal === 'number' ? proj.dailyGoal : 500;
  const dailyGoalCompletion = Math.max(
    0,
    Math.min(100, Math.round((todayWords / dailyGoal) * 100)),
  );

  // Weekly pace rows (last 8 weeks)
  const weeklyRows = useMemo(() => {
    const now = new Date();
    const rows: { date: string; words: number }[] = [];

    for (let i = 7; i >= 0; i--) {
      const start = new Date(now);
      start.setDate(now.getDate() - i * 7);
      const end = new Date(start);
      end.setDate(start.getDate() + 7);

      let sum = 0;
      for (const s of sessions) {
        try {
          if (!s.startTime) continue;
          const d = new Date(s.startTime);
          if (d >= start && d < end) {
            sum += s.wordsAdded ?? s.wordCount ?? 0;
          }
        } catch {
          // ignore bad dates
        }
      }

      rows.push({
        date: start.toLocaleDateString(),
        words: sum,
      });
    }

    return rows;
  }, [sessions]);

  if (!currentProject) {
    return (
      <div className="p-8 text-center">
        <BookOpen className="w-10 h-10 mx-auto text-slate-400 mb-3" />
        <h2 className="text-lg font-semibold">No project selected</h2>
        <p className="text-slate-500">Select a project to view writing analytics.</p>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-8 page-transition">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-xl font-semibold">Writing analytics</h1>
          <p className="text-sm text-slate-500">Insights for "{projectName}"</p>
        </div>
        <select
          value={timeRange}
          onChange={(e) => setTimeRange(e.target.value as any)}
          className="border rounded px-2 py-1 text-sm"
        >
          <option value="week">Last week</option>
          <option value="month">Last month</option>
          <option value="year">Last year</option>
        </select>
      </div>

      {/* Key metrics */}
      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-6">
        <_StatCard
          icon={<Edit3 className="w-4 h-4" />}
          label="Total words"
          value={totalWords.toLocaleString()}
        />
        <_StatCard
          icon={<Clock className="w-4 h-4" />}
          label="Total time"
          value={`${Math.floor(totalWritingTimeMin / 60)}h ${totalWritingTimeMin % 60}m`}
        />
        <_StatCard
          icon={<Target className="w-4 h-4" />}
          label="Daily average"
          value={`${averageWordsPerDay} words`}
        />
        <_StatCard
          icon={<TrendingUp className="w-4 h-4" />}
          label="Streak"
          value={`${writingStreak} days`}
        />
        <_StatCard
          icon={<BookOpen className="w-4 h-4" />}
          label="Sessions"
          value={sessions.length}
        />
        <_StatCard
          icon={<Award className="w-4 h-4" />}
          label="Daily goal"
          value={`${dailyGoalCompletion}%`}
        />
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <div className="rounded-2xl border p-4">
          <div className="flex items-center gap-2 mb-3">
            <Calendar className="w-4 h-4 text-purple-600" />
            <h3 className="font-medium">Words over time</h3>
          </div>
          <PerformanceChart type="trend" rows={trendRows} xKey="date" yKey="words" height={280} />
        </div>

        <div className="rounded-2xl border p-4">
          <div className="flex items-center gap-2 mb-3">
            <Target className="w-4 h-4 text-blue-600" />
            <h3 className="font-medium">Weekly pace</h3>
          </div>
          <PerformanceChart
            type="comparison"
            rows={weeklyRows}
            xKey="date"
            yKey="words"
            height={280}
          />
        </div>

        {/* Phrase Hygiene Widget */}
        <div className="rounded-2xl border-0">
          <PhraseHygieneWidget className="border-0" />
        </div>
      </div>
    </div>
  );
}

function _computeStreak(sessions: Session[]): number {
  try {
    const dayHasWords = new Set<string>();
    for (const s of sessions) {
      if (!s.startTime) continue;
      const dateStr = new Date(s.startTime).toISOString().slice(0, 10);
      const words = s.wordsAdded ?? s.wordCount ?? 0;
      if (words > 0) dayHasWords.add(dateStr);
    }

    let streak = 0;
    const cur = new Date();
    while (true) {
      const key = cur.toISOString().slice(0, 10);
      const isToday = key === new Date().toISOString().slice(0, 10);
      if (dayHasWords.has(key) || isToday) {
        streak++;
        cur.setDate(cur.getDate() - 1);
        continue;
      }
      break;
    }
    return streak;
  } catch {
    return 0;
  }
}

