// src/components/Onboarding/OnboardingOrchestrator.tsx
import { match } from 'path-to-regexp';
import React, { useEffect, useMemo, useRef, useState } from 'react';
import { useLocation } from 'react-router-dom';

import analyticsService from '@/services/analyticsService';

import { useTutorialStorage } from '../../services/tutorialStorage';
import { useIsInRouter } from '../../utils/routerGuards';

import { shouldShowTourPrompt as gatingShouldShow, setPromptedThisSession } from './tourGating';
import TourOverlay from './TourOverlay';

// Allowlist for first-time onboarding - keep this very restrictive
const FIRST_TIME_ALLOWED = ['/dashboard'];

function isFirstTimeAllowed(pathname: string) {
  return FIRST_TIME_ALLOWED.some((p) => match(p, { decode: decodeURIComponent })(pathname));
}

const WELCOME_SHOWN_SESSION_KEY = 'inkwell-welcome-shown-this-session';

import type { TourType } from './steps/Step.types';

/**
 * Minimal, self-contained auto-start logic:
 * - `?tour=` query param: "full" | "feature" | "context"
 * - If no param: auto-start full-onboarding when not completed and not snoozed
 */

export function OnboardingOrchestrator() {
  const inRouter = useIsInRouter();
  // Always call the hook, but only use its result when in a router
  const routerLocation = useLocation();
  // Derive the location object safely
  const location = useMemo(() => {
    return inRouter ? routerLocation : { pathname: window.location.pathname };
  }, [inRouter, routerLocation]);

  const [open, setOpen] = useState(false);
  const [tourType, setTourType] = useState<TourType>('full-onboarding');
  const launchedThisSession = useRef(false);
  const finishedThisSession = useMemo(
    () => sessionStorage.getItem('inkwell:tour:finished') === '1',
    [],
  );
  const { getPreferences, getProgress } = useTutorialStorage?.() ?? {};

  // Mark welcome modal shown once per session when orchestrator first mounts
  useEffect(() => {
    const already = sessionStorage.getItem(WELCOME_SHOWN_SESSION_KEY) === 'true';
    if (!already) {
      sessionStorage.setItem(WELCOME_SHOWN_SESSION_KEY, 'true');
    }
    // One-time cleanup of legacy first-run keys
    try {
      ['inkwell.tour.firstTime', 'tour.firstRun', 'onboarding.seen'].forEach((k) =>
        localStorage.removeItem(k),
      );
    } catch {}
  }, []);

  // Listen for completion, prevent further auto-starts this session
  useEffect(() => {
    const done = () => {
      launchedThisSession.current = true;
      sessionStorage.setItem('inkwell:tour:launched', '1');
    };
    window.addEventListener('inkwell:tour:completed', done);
    return () => window.removeEventListener('inkwell:tour:completed', done);
  }, []);

  // Auto-start logic (URL param, first-run, etc.)
  useEffect(() => {
    (async () => {
      if (launchedThisSession.current || finishedThisSession) return;

      const url = new URL(window.location.href);
      const q = (url.searchParams.get('tour') as TourType | null) ?? null;

      // If we have prefs/progress that say "completed", do NOT autostart
      const slug = q ?? 'full-onboarding';
      try {
        const [prefs, progress] = await Promise.all([getPreferences?.(), getProgress?.(slug)]);
        const isCompleted =
          (prefs?.completedTours ?? []).includes(slug) || progress?.progress?.isCompleted === true;
        if (isCompleted) {
          // also strip ?tour= so refresh doesn't resurrect it
          if (q) {
            url.searchParams.delete('tour');
            window.history.replaceState({}, '', url.toString());
          }
          return;
        }
      } catch {
        // ignore; proceed on best-effort
      }

      if (q) {
        setTourType(q);
        setOpen(true);
        launchedThisSession.current = true;
        try {
          analyticsService.trackEvent('tour_started', {
            tourType: 'first_time',
            entryPoint: 'overlay',
          });
          // Backward-compat: also call .track for tests mocking that API
          (analyticsService as any).track?.('tour_started', {
            tourType: 'first_time',
            entryPoint: 'overlay',
          });
        } catch {}
        // strip the param after opening once
        url.searchParams.delete('tour');
        window.history.replaceState({}, '', url.toString());
        return;
      }

      // First-run gating without URL param - only on allowed routes
      const isFirstRun = localStorage.getItem('inkwell.onboarding.firstRunCompleted') !== 'true';
      if (isFirstRun && isFirstTimeAllowed(location.pathname) && gatingShouldShow()) {
        setTourType('full-onboarding');
        setOpen(true);
        launchedThisSession.current = true;
        try {
          setPromptedThisSession();
        } catch {}
        try {
          analyticsService.trackEvent('tour_started', {
            tourType: 'first_time',
            entryPoint: 'overlay',
          });
          // Backward-compat: also call .track for tests mocking that API
          (analyticsService as any).track?.('tour_started', {
            tourType: 'first_time',
            entryPoint: 'overlay',
          });
        } catch {}
      }
    })();
  }, [getPreferences, getProgress, finishedThisSession]);

  // Programmatic starts (from settings, etc.)
  useEffect(() => {
    const handler = (e: Event) => {
      const ev = e as CustomEvent<{ slug?: TourType }>;
      if (launchedThisSession.current || finishedThisSession) return;
      setTourType(ev.detail?.slug ?? 'full-onboarding');
      setOpen(true);
      launchedThisSession.current = true;
    };
    window.addEventListener('inkwell:tour:start', handler as EventListener);
    return () => window.removeEventListener('inkwell:tour:start', handler as EventListener);
  }, [finishedThisSession]);

  if (!open) return null;
  return (
    <TourOverlay
      tourType={tourType}
      onClose={() => {
        try {
          // Mark first-run tour completed on successful completion
          if (tourType === 'full-onboarding') {
            localStorage.setItem('inkwell.onboarding.firstRunCompleted', 'true');
          }
        } catch {}
        setOpen(false);
      }}
    />
  );
}

export default OnboardingOrchestrator;

// src/components/Onboarding/TourOverlay.tsx
import React, { useCallback, useEffect, useMemo, useRef, useState } from 'react';
import { createPortal } from 'react-dom';

import { analyticsService } from '../../services/analyticsService';
import { useTutorialStorage } from '../../services/tutorialStorage';

import { loadTourPreset } from './presetLoaderHelper';

import type { TourType } from './steps/Step.types';

type Props = {
  tourType?: TourType;
  onClose?: () => void;
  /** Optional custom persistence key; defaults to "tour:<tourType>" */
  persistKey?: string;
};

export default function TourOverlay({ tourType = 'full-onboarding', onClose, persistKey }: Props) {
  const mounted = useRef(false);
  const profileId = ''; // TODO: Get this from context/props if needed
  const steps = useMemo(() => loadTourPreset(tourType), [tourType]);
  const total = steps.length;
  const [i, setI] = useState(0);
  const storage = useTutorialStorage?.() ?? {};

  const stripTourQueryParam = useCallback(() => {
    try {
      const url = new URL(window.location.href);
      if (url.searchParams.has('tour')) {
        url.searchParams.delete('tour');
        window.history.replaceState({}, '', url.toString());
      }
    } catch {}
  }, []);

  const onNext = useCallback(() => setI((x) => Math.min(total - 1, x + 1)), [total]);
  const onPrevious = useCallback(() => setI((x) => Math.max(0, x - 1)), []);
  const onComplete = useCallback(async () => {
    const slug = persistKey || tourType; // use persistKey if provided, otherwise use tourType as the slug/key
    const now = Date.now();
    try {
      // 1) persist "completed" progress
      if (storage.setProgress) {
        await storage.setProgress(slug, {
          currentStep: total,
          completedSteps: Array.from({ length: total }, (_, k) => String(k)),
          tourType,
          startedAt: now,
          completedAt: now,
          isCompleted: true,
          totalSteps: total,
          lastActiveAt: now,
        });
      }
      // 2) mark preference completed to short-circuit future auto-starts
      if (storage.setPreferences) {
        await storage.setPreferences({
          neverShowAgain: false,
          remindMeLater: false,
          completedTours: [slug],
          tourDismissals: 0,
        });
      }
      // 3) mark this session as "tour finished" to stop any session-based triggers
      sessionStorage.setItem('inkwell:tour:finished', '1');
      // 4) clear ?tour= param so history/refresh won't retrigger
      stripTourQueryParam();
      // 5) broadcast completion (orchestrator listens)
      window.dispatchEvent(new CustomEvent('inkwell:tour:completed', { detail: { slug } }));
    } catch {
      // non-blocking; still close the overlay
    }
    onClose?.();
  }, [onClose, storage, stripTourQueryParam, tourType, total]);

  // Log tour start on first mount
  useEffect(() => {
    if (import.meta.env.DEV) {
      console.info('[tour-overlay] tour starting', {
        tourType,
        profileId,
        storage: !!storage,
      });
    }
    try {
      if (import.meta.env.DEV) {
        console.info('[tour-overlay] starting tour:', {
          tourType,
          profileId,
          storage: !!storage,
        });
      }
      const base = {
        tourType: tourType === 'full-onboarding' ? 'first_time' : 'feature_tour',
        entryPoint: 'overlay',
      } as const;
      const payload = profileId ? { ...base, profileId } : base;
      analyticsService.track('tour_started', payload as any);
    } catch {}
  }, [tourType, profileId]); // Include dependencies

  // Log step changes
  useEffect(() => {
    try {
      analyticsService.trackTourStepCompleted(tourType, i, `step-${i}`, Date.now());
    } catch {}
  }, [i, tourType, profileId, total]);

  // Load last progress (if any) on first mount
  useEffect(() => {
    let cancelled = false;
    (async () => {
      try {
        const p = await storage.getProgress?.(persistKey || tourType);
        if (!cancelled && p && !(p as any).isCompleted) {
          setI(Math.min(total - 1, Math.max(0, (p as any).progress?.currentStep ?? 0)));
        }
      } catch {
        /* no-op: fall back to step 0 */
      }
    })();
    return () => {
      cancelled = true;
    };
  }, [persistKey, tourType, total]);

  // Persist step changes (debounced per render tick)
  useEffect(() => {
    // skip the very first paint (before we've checked existing progress)
    if (!mounted.current) {
      mounted.current = true;
      return;
    }
    try {
      void storage.setProgress?.(persistKey || tourType, {
        currentStep: i,
        completedSteps: [],
        tourType,
        startedAt: Date.now(),
        isCompleted: false,
        totalSteps: total,
        lastActiveAt: Date.now(),
      });
    } catch {}
  }, [i, persistKey, tourType, total, profileId, storage]);

  const Step = steps[i];
  if (!Step) return null;

  return createPortal(
    <div className="fixed inset-0 z-[100] bg-black/30">
      <div className="absolute inset-x-0 bottom-0 md:inset-auto md:top-1/2 md:left-1/2 md:-translate-x-1/2 md:-translate-y-1/2">
        <div className="bg-white rounded-2xl shadow-xl mx-auto md:min-w-[560px]">
          <Step
            stepIndex={i}
            totalSteps={total}
            onNext={onNext}
            onPrevious={onPrevious}
            onComplete={onComplete}
          />
        </div>
      </div>
    </div>,
    document.body,
  );
}

