// src/components/Recovery/StorageRecoveryBanner.tsx
import devLog from "src/utils/devLogger";
import { AlertTriangle, Download, Trash2, RefreshCw, X } from 'lucide-react';
import React, { useState, useEffect } from 'react';

import { useToast } from '@/context/toast';

import { exportService } from '../../services/exportService';
import { snapshotService } from '../../services/snapshotService';
import { quotaAwareStorage, StorageQuotaInfo, StorageError } from '../../utils/quotaAwareStorage';

interface StorageRecoveryBannerProps {
  onDismiss?: () => void;
}

export const StorageRecoveryBanner: React.FC<StorageRecoveryBannerProps> = ({ onDismiss }) => {
  const [quotaInfo, setQuotaInfo] = useState<StorageQuotaInfo | null>(null);
  const [storageError, setStorageError] = useState<StorageError | null>(null);
  const [isCleaningUp, setIsCleaningUp] = useState(false);
  const [isDownloading, setIsDownloading] = useState(false);
  const [showDetails, setShowDetails] = useState(false);
  const { showToast } = useToast();

  useEffect(() => {
    // Monitor quota changes
    const unsubscribeQuota = quotaAwareStorage.onQuotaUpdate((info) => {
      setQuotaInfo(info);
    });

    // Monitor storage errors
    const unsubscribeError = quotaAwareStorage.onStorageError((error) => {
      setStorageError(error);
    });

    // Initial quota check
    quotaAwareStorage.getQuotaInfo().then(setQuotaInfo);

    return () => {
      unsubscribeQuota();
      unsubscribeError();
    };
  }, []);

  const handleEmergencyCleanup = async () => {
    setIsCleaningUp(true);
    try {
      const result = await quotaAwareStorage.emergencyCleanup();
      showToast(`Cleanup complete! Freed ${(result.freedBytes / 1024).toFixed(1)}KB`, 'success');

      if (result.actions.length > 0) {
        devLog.debug('Cleanup actions performed:', result.actions);
      }

      // Refresh quota info
      const newQuotaInfo = await quotaAwareStorage.getQuotaInfo();
      setQuotaInfo(newQuotaInfo);

      if (!newQuotaInfo.isCritical) {
        setStorageError(null);
      }
    } catch (error) {
      console.error('Cleanup failed:', error);
      showToast('Cleanup failed. Please try manual backup.', 'error');
    } finally {
      setIsCleaningUp(false);
    }
  };

  const handleDownloadBackup = async () => {
    setIsDownloading(true);
    try {
      // Export all projects as backup
      const projects = JSON.parse(localStorage.getItem('inkwell_enhanced_projects') || '[]');
      if (projects.length > 0) {
        for (const project of projects) {
          await exportService.exportProject(project.id, 'json');
        }
        showToast('Backup downloaded successfully', 'success');
      } else {
        showToast('No projects found to backup', 'warning');
      }
    } catch (error) {
      console.error('Backup download failed:', error);
      showToast('Backup download failed', 'error');
    } finally {
      setIsDownloading(false);
    }
  };

  const handleClearSnapshots = async () => {
    try {
      const projects = JSON.parse(localStorage.getItem('inkwell_enhanced_projects') || '[]');
      let totalCleared = 0;

      for (const project of projects) {
        const cleared = await snapshotService.emergencyCleanup(project.id, 2);
        totalCleared += cleared;
      }

      showToast(`Cleared ${totalCleared} old snapshots`, 'success');

      // Refresh quota info
      const newQuotaInfo = await quotaAwareStorage.getQuotaInfo();
      setQuotaInfo(newQuotaInfo);
    } catch (error) {
      console.error('Failed to clear snapshots:', error);
      showToast('Failed to clear snapshots', 'error');
    }
  };

  // Don't show banner if no issues
  if (!quotaInfo || (!quotaInfo.isNearLimit && !storageError)) {
    return null;
  }
  const _isQuotaIssue = quotaInfo.isCritical || quotaInfo.isNearLimit;
  const _isStorageError = storageError !== null;

  return (
    <div
      className={`
      fixed top-0 left-0 right-0 z-50 
      ${quotaInfo.isCritical || storageError?.type === 'quota' ? 'bg-red-600' : 'bg-yellow-600'} 
      text-white shadow-lg border-b-2 border-opacity-20 border-white
    `}
    >
      <div className="max-w-7xl mx-auto px-4 py-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <AlertTriangle className="h-5 w-5 flex-shrink-0" />
            <div className="flex-1">
              <div className="font-semibold">
                {storageError
                  ? storageError.type === 'quota'
                    ? 'Storage Full'
                    : 'Storage Error'
                  : quotaInfo.isCritical
                    ? 'Storage Critical'
                    : 'Storage Warning'}
              </div>
              <div className="text-sm opacity-90">
                {storageError
                  ? storageError.message
                  : quotaInfo.isCritical
                    ? `Storage is ${(quotaInfo.percentUsed * 100).toFixed(1)}% full. Save your work immediately.`
                    : `Storage is ${(quotaInfo.percentUsed * 100).toFixed(1)}% full. Consider cleaning up.`}
              </div>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            {/* Action buttons */}
            <button
              onClick={handleDownloadBackup}
              disabled={isDownloading}
              className="px-3 py-1 bg-white bg-opacity-20 hover:bg-opacity-30 rounded text-sm font-medium transition-colors disabled:opacity-50"
            >
              {isDownloading ? (
                <RefreshCw className="h-4 w-4 animate-spin" />
              ) : (
                <>
                  <Download className="h-4 w-4 inline mr-1" />
                  Backup
                </>
              )}
            </button>

            <button
              onClick={handleEmergencyCleanup}
              disabled={isCleaningUp}
              className="px-3 py-1 bg-white bg-opacity-20 hover:bg-opacity-30 rounded text-sm font-medium transition-colors disabled:opacity-50"
            >
              {isCleaningUp ? (
                <RefreshCw className="h-4 w-4 animate-spin" />
              ) : (
                <>
                  <Trash2 className="h-4 w-4 inline mr-1" />
                  Cleanup
                </>
              )}
            </button>

            <button
              onClick={() => setShowDetails(!showDetails)}
              className="px-3 py-1 bg-white bg-opacity-20 hover:bg-opacity-30 rounded text-sm font-medium transition-colors"
            >
              {showDetails ? 'Hide' : 'Details'}
            </button>

            {onDismiss && (
              <button
                onClick={onDismiss}
                className="p-1 hover:bg-white hover:bg-opacity-20 rounded transition-colors"
              >
                <X className="h-4 w-4" />
              </button>
            )}
          </div>
        </div>

        {/* Detailed view */}
        {showDetails && quotaInfo && (
          <div className="mt-3 pt-3 border-t border-white border-opacity-20">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
              <div>
                <div className="font-medium">Storage Usage</div>
                <div className="opacity-90">
                  {(quotaInfo.usage / 1024 / 1024).toFixed(1)}MB /{' '}
                  {(quotaInfo.quota / 1024 / 1024).toFixed(1)}MB
                </div>
                <div className="w-full bg-white bg-opacity-20 rounded-full h-2 mt-1">
                  <div
                    className="bg-white h-2 rounded-full transition-all"
                    style={{ width: `${Math.min(quotaInfo.percentUsed * 100, 100)}%` }}
                  />
                </div>
              </div>

              <div>
                <div className="font-medium">Available Space</div>
                <div className="opacity-90">
                  {(quotaInfo.available / 1024 / 1024).toFixed(1)}MB remaining
                </div>
              </div>

              <div>
                <div className="font-medium">Suggested Actions</div>
                <div className="opacity-90">
                  {storageError?.suggestedActions ? (
                    <ul className="text-xs space-y-1">
                      {storageError.suggestedActions.slice(0, 2).map((action, index) => (
                        <li key={index}>â€¢ {action}</li>
                      ))}
                    </ul>
                  ) : (
                    <div className="space-y-1">
                      <button
                        onClick={handleClearSnapshots}
                        className="block text-xs hover:underline"
                      >
                        â€¢ Clear old snapshots
                      </button>
                      <button
                        onClick={handleDownloadBackup}
                        className="block text-xs hover:underline"
                      >
                        â€¢ Download backup
                      </button>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// Offline Banner Component
interface OfflineBannerProps {
  queuedOperations: number;
  onViewQueue?: () => void;
  onDismiss?: () => void;
}

export const OfflineBanner: React.FC<OfflineBannerProps> = ({
  queuedOperations,
  onViewQueue,
  onDismiss,
}) => {
  if (queuedOperations === 0) return null;

  return (
    <div className="fixed top-0 left-0 right-0 z-40 bg-orange-600 text-white shadow-lg">
      <div className="max-w-7xl mx-auto px-4 py-2">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="h-2 w-2 bg-white rounded-full animate-pulse"></div>
            <div className="text-sm">
              <span className="font-medium">Offline Mode</span>
              <span className="opacity-90 ml-2">{queuedOperations} operations queued for sync</span>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            {onViewQueue && (
              <button
                onClick={onViewQueue}
                className="px-2 py-1 bg-white bg-opacity-20 hover:bg-opacity-30 rounded text-xs font-medium transition-colors"
              >
                View Queue
              </button>
            )}
            {onDismiss && (
              <button
                onClick={onDismiss}
                className="p-1 hover:bg-white hover:bg-opacity-20 rounded transition-colors"
              >
                <X className="h-3 w-3" />
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

// Hook for using storage recovery
export const useStorageRecovery = () => {
  const [showRecoveryBanner, setShowRecoveryBanner] = useState(false);
  const [quotaInfo, setQuotaInfo] = useState<StorageQuotaInfo | null>(null);

  useEffect(() => {
    const unsubscribe = quotaAwareStorage.onQuotaUpdate((info) => {
      setQuotaInfo(info);
      setShowRecoveryBanner(info.isNearLimit || info.isCritical);
    });

    const unsubscribeError = quotaAwareStorage.onStorageError(() => {
      setShowRecoveryBanner(true);
    });

    // Initial check
    quotaAwareStorage.getQuotaInfo().then((info) => {
      setQuotaInfo(info);
      setShowRecoveryBanner(info.isNearLimit || info.isCritical);
    });

    return () => {
      unsubscribe();
      unsubscribeError();
    };
  }, []);

  return {
    showRecoveryBanner,
    quotaInfo,
    dismissRecoveryBanner: () => setShowRecoveryBanner(false),
  };
};

/**
 * Preview Guard Component
 * Ensures preview routes are only accessible when:
 * 1. Free preview feature flag is enabled
 * 2. User is NOT authenticated
 *
 * - If flag is off â†’ redirects to /signup
 * - If user is logged in â†’ redirects to /dashboard
 * - Otherwise â†’ allows access to preview
 */

import React from 'react';
import { Navigate } from 'react-router-dom';

import { useAuth } from '@/context/AuthContext';
import { isPreviewModeEnabled } from '@/features/preview/isPreviewRoute';

export interface PreviewGuardProps {
  children: React.ReactNode;
}

export function PreviewGuard({ children }: PreviewGuardProps) {
  const { user, loading } = useAuth();
  const isPreviewEnabled = isPreviewModeEnabled();

  // While loading auth status, show nothing to prevent flashing
  if (loading) {
    return null;
  }

  // If feature flag is off, redirect to signup
  if (!isPreviewEnabled) {
    console.info('[PreviewGuard] Feature flag disabled, redirecting to signup');
    return <Navigate to="/signup" replace />;
  }

  // If user is authenticated, redirect to dashboard
  if (user) {
    console.info('[PreviewGuard] User authenticated, redirecting to dashboard');
    return <Navigate to="/dashboard" replace />;
  }

  // User is unauthenticated and feature flag is on - allow preview access
  return <>{children}</>;
}

export default PreviewGuard;

// src/components/Search/SmartSearchModal.tsx
import {
  Search,
  X,
  Filter,
  Star,
  Sparkles,
  BookOpen,
  User,
  Map,
  FileText,
  Edit3,
  Eye,
  Link,
  ArrowUpRight,
  Loader2,
  Settings,
  History,
  Bookmark,
  TrendingUp,
} from 'lucide-react';
import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';

import { useAppContext } from '@/context/AppContext';
import { useToast } from '@/context/toast';

import { cn } from '../../lib/utils';
import {
  smartSearchService,
  SmartSearchResult,
  SearchSuggestion,
  SmartSearchOptions,
  SavedSearch,
} from '../../services/smartSearchService';

interface SmartSearchModalProps {
  isOpen: boolean;
  onClose: () => void;
  onNavigate?: (_result: SmartSearchResult) => void;
  initialQuery?: string;
  focusMode?: 'research' | 'writing' | 'editing' | 'analysis';
}

type SearchView = 'search' | 'filters' | 'history' | 'saved';

export const SmartSearchModal: React.FC<SmartSearchModalProps> = ({
  isOpen,
  onClose,
  onNavigate,
  initialQuery = '',
  focusMode,
}) => {
  const { currentProject } = useAppContext();
  const { showToast } = useToast();

  // Search state
  const [query, setQuery] = useState(initialQuery);
  const [isSearching, setIsSearching] = useState(false);
  const [results, setResults] = useState<SmartSearchResult[]>([]);
  const [suggestions, setSuggestions] = useState<SearchSuggestion[]>([]);
  const [totalCount, setTotalCount] = useState(0);
  const [queryTime, setQueryTime] = useState(0);
  const [hasMore, setHasMore] = useState(false);

  // UI state
  const [currentView, setCurrentView] = useState<SearchView>('search');
  const [selectedResultIndex, setSelectedResultIndex] = useState(-1);
  const [selectedSuggestionIndex, setSelectedSuggestionIndex] = useState(-1);
  const [_showFilters, _setShowFilters] = useState(false);

  // Filter state
  const [filters, setFilters] = useState<SmartSearchOptions>({
    types: ['scene', 'chapter', 'character', 'plot'],
    maxResults: 20,
    minScore: 0.1,
    enableSemanticSearch: true,
    enableAdvancedQuery: true,
    ...(focusMode ? { userIntent: focusMode } : {}),
    projectId: currentProject?.id || '',
  });

  // Refs
  const inputRef = useRef<HTMLInputElement>(null);
  const resultsRef = useRef<HTMLDivElement>(null);
  const debounceTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Initialize search service
  useEffect(() => {
    if (isOpen && currentProject?.id) {
      smartSearchService.initialize(currentProject.id);
    }
  }, [isOpen, currentProject?.id]);

  // Focus input when opened
  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isOpen]);

  // Handle query changes with debounce
  const handleQueryChange = useCallback(
    (newQuery: string) => {
      setQuery(newQuery);
      setSelectedResultIndex(-1);
      setSelectedSuggestionIndex(-1);

      // Clear previous timeout
      if (debounceTimeoutRef.current) {
        clearTimeout(debounceTimeoutRef.current);
      }

      // Debounce search
      debounceTimeoutRef.current = setTimeout(() => {
        if (newQuery.trim() || newQuery === '') {
          performSearch(newQuery, filters);
        }
      }, 300);
    },
    [filters],
  );

  // Perform search
  const performSearch = useCallback(
    async (searchQuery: string, searchOptions: SmartSearchOptions) => {
      if (!currentProject?.id) return;

      setIsSearching(true);

      try {
        const searchResult = await smartSearchService.search(searchQuery, {
          ...searchOptions,
          projectId: currentProject.id,
          contextProject: currentProject as any, // TODO: Fix type conversion from Project to EnhancedProject
        });

        setResults(searchResult.results);
        setSuggestions(searchResult.suggestions);
        setTotalCount(searchResult.totalCount);
        setQueryTime(searchResult.queryTime);
        setHasMore(searchResult.hasMore);
      } catch (error) {
        console.error('Search failed:', error);
        showToast('Search failed. Please try again.', 'error');
        setResults([]);
        setSuggestions([]);
      } finally {
        setIsSearching(false);
      }
    },
    [currentProject, showToast],
  );

  // Handle filter changes
  const handleFilterChange = useCallback(
    (newFilters: Partial<SmartSearchOptions>) => {
      const updatedFilters = { ...filters, ...newFilters };
      setFilters(updatedFilters);

      if (query.trim()) {
        performSearch(query, updatedFilters);
      }
    },
    [filters, query, performSearch],
  );

  // Handle suggestion click
  const handleSuggestionClick = useCallback(
    (suggestion: SearchSuggestion) => {
      setQuery(suggestion.query);
      performSearch(suggestion.query, filters);
      setSelectedSuggestionIndex(-1);
    },
    [filters, performSearch],
  );

  // Handle result click
  const handleResultClick = useCallback(
    (result: SmartSearchResult) => {
      onNavigate?.(result);
      onClose();
    },
    [onNavigate, onClose],
  );

  // Handle quick action
  const handleQuickAction = useCallback(
    (action: () => void) => {
      action();
      onClose();
    },
    [onClose],
  );

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (!isOpen) return;

      switch (e.key) {
        case 'Escape':
          e.preventDefault();
          onClose();
          break;

        case 'ArrowDown':
          e.preventDefault();
          if (suggestions.length > 0 && selectedSuggestionIndex < suggestions.length - 1) {
            setSelectedSuggestionIndex((prev) => prev + 1);
            setSelectedResultIndex(-1);
          } else if (results.length > 0 && selectedResultIndex < results.length - 1) {
            setSelectedResultIndex((prev) => prev + 1);
            setSelectedSuggestionIndex(-1);
          }
          break;

        case 'ArrowUp':
          e.preventDefault();
          if (selectedResultIndex > 0) {
            setSelectedResultIndex((prev) => prev - 1);
          } else if (selectedSuggestionIndex > 0) {
            setSelectedSuggestionIndex((prev) => prev - 1);
          } else if (selectedResultIndex === 0) {
            setSelectedResultIndex(-1);
            setSelectedSuggestionIndex(suggestions.length - 1);
          }
          break;

        case 'Enter':
          e.preventDefault();
          if (selectedSuggestionIndex >= 0 && suggestions[selectedSuggestionIndex]) {
            handleSuggestionClick(suggestions[selectedSuggestionIndex]);
          } else if (selectedResultIndex >= 0 && results[selectedResultIndex]) {
            handleResultClick(results[selectedResultIndex]);
          } else if (query.trim()) {
            performSearch(query, filters);
          }
          break;

        case 'Tab':
          if (e.shiftKey) {
            e.preventDefault();
            setCurrentView((prev) => {
              const views: SearchView[] = ['search', 'filters', 'history', 'saved'];
              const currentIndex = views.indexOf(prev);
              return views[currentIndex > 0 ? currentIndex - 1 : views.length - 1] as SearchView;
            });
          } else {
            e.preventDefault();
            setCurrentView((prev) => {
              const views: SearchView[] = ['search', 'filters', 'history', 'saved'];
              const currentIndex = views.indexOf(prev);
              return views[(currentIndex + 1) % views.length] as SearchView;
            });
          }
          break;
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [
    isOpen,
    onClose,
    suggestions,
    results,
    selectedSuggestionIndex,
    selectedResultIndex,
    handleSuggestionClick,
    handleResultClick,
    query,
    filters,
    performSearch,
  ]);

  // Get search history
  const searchHistory = useMemo(() => {
    return smartSearchService.getSearchHistory();
  }, [currentView]);

  // Saved searches
  const savedSearches = useMemo(() => {
    return smartSearchService.getSavedSearches();
  }, [currentView]);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 bg-black/50 backdrop-blur-sm flex items-start justify-center pt-20">
      <div className="bg-white dark:bg-gray-800 rounded-xl shadow-2xl w-full max-w-4xl mx-4 max-h-[80vh] flex flex-col overflow-hidden border border-gray-200 dark:border-gray-700">
        {/* Header */}
        <div className="flex items-center gap-4 p-4 border-b border-gray-200 dark:border-gray-700">
          <div className="flex items-center gap-2 flex-1">
            <Search className="w-5 h-5 text-gray-500" />
            <input
              ref={inputRef}
              type="text"
              value={query}
              onChange={(e) => handleQueryChange(e.target.value)}
              placeholder={`Search your ${currentProject?.name || 'project'}...`}
              className="flex-1 bg-transparent border-none outline-none text-lg placeholder-gray-500"
            />
            {isSearching && <Loader2 className="w-4 h-4 text-blue-500 animate-spin" />}
          </div>

          <div className="flex items-center gap-2">
            {/* View tabs */}
            <div className="flex items-center bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
              <button
                onClick={() => setCurrentView('search')}
                className={cn(
                  'px-3 py-1 text-sm rounded-md transition-colors',
                  currentView === 'search'
                    ? 'bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow-sm'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white',
                )}
              >
                Search
              </button>
              <button
                onClick={() => setCurrentView('filters')}
                className={cn(
                  'px-3 py-1 text-sm rounded-md transition-colors',
                  currentView === 'filters'
                    ? 'bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow-sm'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white',
                )}
              >
                <Filter className="w-3 h-3 mr-1 inline" />
                Filters
              </button>
              <button
                onClick={() => setCurrentView('history')}
                className={cn(
                  'px-3 py-1 text-sm rounded-md transition-colors',
                  currentView === 'history'
                    ? 'bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow-sm'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white',
                )}
              >
                <History className="w-3 h-3 mr-1 inline" />
                History
              </button>
              <button
                onClick={() => setCurrentView('saved')}
                className={cn(
                  'px-3 py-1 text-sm rounded-md transition-colors',
                  currentView === 'saved'
                    ? 'bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow-sm'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white',
                )}
              >
                <Bookmark className="w-3 h-3 mr-1 inline" />
                Saved
              </button>
            </div>

            <button
              onClick={onClose}
              className="p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
            >
              <X className="w-4 h-4" />
            </button>
          </div>
        </div>

        {/* Content */}
        <div className="flex-1 flex overflow-hidden">
          {/* Main content area */}
          <div className="flex-1 flex flex-col overflow-hidden">
            {/* Search View */}
            {currentView === 'search' && (
              <>
                {/* Query stats */}
                {(results.length > 0 || query.trim()) && (
                  <div className="px-4 py-2 border-b border-gray-100 dark:border-gray-700 text-sm text-gray-600 dark:text-gray-400">
                    {query.trim() ? (
                      <>
                        {totalCount} results for "{query}"
                        {queryTime > 0 && ` (${queryTime.toFixed(0)}ms)`}
                        {hasMore && ' â€¢ More results available'}
                      </>
                    ) : (
                      'Start typing to search...'
                    )}
                  </div>
                )}

                {/* Suggestions */}
                {suggestions.length > 0 && (
                  <div className="p-4 border-b border-gray-100 dark:border-gray-700">
                    <h3 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-2 flex items-center gap-2">
                      <Sparkles className="w-4 h-4" />
                      Suggestions
                    </h3>
                    <div className="flex flex-wrap gap-2">
                      {suggestions.map((suggestion, index) => (
                        <button
                          key={suggestion.id}
                          onClick={() => handleSuggestionClick(suggestion)}
                          className={cn(
                            'px-3 py-1 text-sm rounded-full transition-colors flex items-center gap-2',
                            selectedSuggestionIndex === index
                              ? 'bg-blue-100 dark:bg-blue-900/20 text-blue-700 dark:text-blue-300'
                              : 'bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-600',
                          )}
                        >
                          {suggestion.type === 'correction' && 'âœ¨'}
                          {suggestion.type === 'semantic' && 'ðŸ§ '}
                          {suggestion.type === 'completion' && 'ðŸ’¡'}
                          {suggestion.type === 'related' && 'ðŸ”—'}
                          {suggestion.query}
                          {suggestion.preview && (
                            <span className="text-xs opacity-75">â€¢ {suggestion.preview}</span>
                          )}
                        </button>
                      ))}
                    </div>
                  </div>
                )}

                {/* Results */}
                <div ref={resultsRef} className="flex-1 overflow-y-auto">
                  {results.length > 0 ? (
                    <div className="p-4 space-y-3">
                      {results.map((result, index) => (
                        <SearchResultCard
                          key={result.id}
                          result={result}
                          isSelected={selectedResultIndex === index}
                          onClick={() => handleResultClick(result)}
                          onQuickAction={handleQuickAction}
                        />
                      ))}
                    </div>
                  ) : query.trim() && !isSearching ? (
                    <div className="flex-1 flex items-center justify-center p-8">
                      <div className="text-center">
                        <Search className="w-12 h-12 text-gray-300 dark:text-gray-600 mx-auto mb-4" />
                        <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                          No results found
                        </h3>
                        <p className="text-gray-600 dark:text-gray-400">
                          Try adjusting your search terms or filters
                        </p>
                      </div>
                    </div>
                  ) : (
                    <div className="flex-1 flex items-center justify-center p-8">
                      <div className="text-center">
                        <Search className="w-12 h-12 text-gray-300 dark:text-gray-600 mx-auto mb-4" />
                        <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                          Search your project
                        </h3>
                        <p className="text-gray-600 dark:text-gray-400 mb-4">
                          Find scenes, characters, plot notes, and more
                        </p>
                        <div className="text-xs text-gray-500 space-y-1">
                          <p>â€¢ Use quotes for exact phrases: "dark forest"</p>
                          <p>â€¢ Use + for required terms: magic +wizard</p>
                          <p>â€¢ Use - to exclude terms: character -villain</p>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </>
            )}

            {/* Filters View */}
            {currentView === 'filters' && (
              <SearchFiltersPanel filters={filters} onFiltersChange={handleFilterChange} />
            )}

            {/* History View */}
            {currentView === 'history' && (
              <SearchHistoryPanel
                searchHistory={searchHistory}
                onQuerySelect={(query) => {
                  setQuery(query);
                  setCurrentView('search');
                  performSearch(query, filters);
                }}
              />
            )}

            {/* Saved View */}
            {currentView === 'saved' && (
              <SavedSearchesPanel
                savedSearches={savedSearches}
                onSearchSelect={(saved) => {
                  setQuery(saved.query);
                  setFilters({ ...filters, ...saved.options });
                  setCurrentView('search');
                  performSearch(saved.query, { ...filters, ...saved.options });
                }}
                onSaveCurrentSearch={() => {
                  if (query.trim()) {
                    const name = prompt('Save this search as:');
                    if (name) {
                      smartSearchService.saveSearch(name, query, filters);
                      showToast('Search saved successfully', 'success');
                    }
                  }
                }}
              />
            )}
          </div>
        </div>

        {/* Footer */}
        <div className="px-4 py-3 border-t border-gray-200 dark:border-gray-700 text-xs text-gray-500 dark:text-gray-400 flex items-center justify-between">
          <div className="flex items-center gap-4">
            <span>Use â†‘â†“ to navigate, Enter to select, Tab to switch views</span>
            {filters.enableSemanticSearch && (
              <span className="flex items-center gap-1">
                <Sparkles className="w-3 h-3" />
                AI-powered search enabled
              </span>
            )}
          </div>

          <div className="flex items-center gap-2">
            {currentView === 'search' && query.trim() && (
              <button
                onClick={() => {
                  const name = prompt('Save this search as:');
                  if (name) {
                    smartSearchService.saveSearch(name, query, filters);
                    showToast('Search saved successfully', 'success');
                  }
                }}
                className="text-blue-500 hover:text-blue-600 transition-colors"
              >
                <Star className="w-3 h-3" />
              </button>
            )}
            <span>Esc to close</span>
          </div>
        </div>
      </div>
    </div>
  );
};

