import devLog from "src/utils/devLogger";
import { clsx } from 'clsx';
import { Bell } from 'lucide-react';
import { useState } from 'react';

import type { Notification } from '@/types/notifications';

import { NotificationsPanel } from './NotificationsPanel';

// Mock notifications - replace with real data later
const mockNotifications: Notification[] = [
  {
    id: '1',
    title: 'Welcome to Inkwell',
    message: 'Get started with our quick tour of features.',
    type: 'info',
    read: false,
    createdAt: new Date(Date.now() - 1000 * 60 * 5), // 5 minutes ago
  },
  {
    id: '2',
    title: 'Autosave Enabled',
    message: 'Your work will now be saved automatically.',
    type: 'success',
    read: false,
    createdAt: new Date(Date.now() - 1000 * 60 * 15), // 15 minutes ago
  },
];

interface TopbarBellProps {
  className?: string;
}

export default function TopbarBell({ className = '' }: TopbarBellProps) {
  // State
  const [notifications] = useState<Notification[]>(mockNotifications);

  // Handlers
  const handleMarkAsRead = (id: string) => {
    devLog.debug('Mark as read:', id);
  };

  const handleMarkAllAsRead = () => {
    devLog.debug('Mark all as read');
  };

  const handleNotificationClick = (notification: Notification) => {
    devLog.debug('Clicked notification:', notification);
  };
  const [open, setOpen] = useState(false);

  return (
    <>
      <button
        aria-label="Notifications"
        onClick={() => setOpen(true)}
        className={`relative rounded-full p-2 text-gray-500 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-gray-800 transition-colors ${className}`}
      >
        <Bell className="w-5 h-5" />

        {/* Notification badge */}
        {notifications.some((n) => !n.read) && (
          <span
            className={clsx(
              'absolute -right-1 -top-1 flex h-5 w-5 items-center justify-center',
              'rounded-full bg-red-600 text-xs font-medium text-white dark:text-gray-100',
            )}
          >
            {notifications.filter((n) => !n.read).length}
          </span>
        )}
      </button>

      {open && (
        <NotificationsPanel
          notifications={notifications}
          onClose={() => setOpen(false)}
          onMarkAsRead={handleMarkAsRead}
          onMarkAllAsRead={handleMarkAllAsRead}
          onNotificationClick={handleNotificationClick}
        />
      )}
    </>
  );
}

// src/components/Writing/EnhancedAIWritingToolbar.tsx - Advanced AI-powered writing assistance toolbar
import {
  Sparkles,
  Heart,
  MessageSquare,
  TrendingUp,
  Palette,
  Brain,
  Settings,
  Play,
  Pause,
  BarChart3,
  Lightbulb,
  RefreshCw,
  Check,
  X,
} from 'lucide-react';
import React, { useState, useEffect, useCallback, useRef } from 'react';

import { useAppContext } from '@/context/AppContext';
import { useToast } from '@/context/toast';
import claudeService from '@/services/claudeService';

interface EnhancedAIWritingToolbarProps {
  selectedText?: string;
  onInsertText?: (text: string, replaceSelection?: boolean) => void;
  sceneTitle?: string;
  currentContent?: string;
  projectContext?: string;
  position?: 'panel' | 'popup';
  onClose?: () => void;
  className?: string;
}

interface AdvancedTool {
  id: string;
  name: string;
  icon: React.ComponentType;
  category: 'tone' | 'pacing' | 'emotion' | 'dialogue' | 'analysis';
  description: string;
  requiresSelection?: boolean;
  isRealTime?: boolean;
  isPremium?: boolean;
}

interface AIAnalysisResult {
  score: number;
  issues: string[];
  suggestions: string[];
  improvements: string[];
  confidence: number;
}

interface ToneAdjustmentResult {
  originalTone: string;
  newTone: string;
  adjustedText: string;
  explanation: string;
}

const TONE_OPTIONS = [
  { id: 'mysterious', label: 'Mysterious', description: 'Dark, suspenseful, intriguing' },
  { id: 'romantic', label: 'Romantic', description: 'Warm, intimate, emotional' },
  { id: 'action', label: 'Action-Packed', description: 'Fast-paced, intense, dynamic' },
  { id: 'comedic', label: 'Comedic', description: 'Light, humorous, entertaining' },
  { id: 'dramatic', label: 'Dramatic', description: 'Serious, emotional, impactful' },
  { id: 'contemplative', label: 'Contemplative', description: 'Thoughtful, reflective, deep' },
  { id: 'tense', label: 'Tense', description: 'Anxious, suspenseful, on-edge' },
  { id: 'melancholic', label: 'Melancholic', description: 'Sad, wistful, bittersweet' },
];

const EMOTION_LEVELS = [
  { id: 'subtle', label: 'Subtle', intensity: 1 },
  { id: 'moderate', label: 'Moderate', intensity: 2 },
  { id: 'strong', label: 'Strong', intensity: 3 },
  { id: 'intense', label: 'Intense', intensity: 4 },
];

export default function EnhancedAIWritingToolbar({
  selectedText = '',
  onInsertText,
  sceneTitle = '',
  currentContent = '',
  projectContext = '',
  position = 'panel',
  onClose,
  className = '',
}: EnhancedAIWritingToolbarProps) {
  const { claudeActions: _claudeActions } = useAppContext();
  const { showToast } = useToast();

  // State management
  const [activeCategory, setActiveCategory] = useState<
    'tone' | 'pacing' | 'emotion' | 'dialogue' | 'analysis'
  >('tone');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResults, setAnalysisResults] = useState<Record<string, AIAnalysisResult>>({});
  const [generatedContent, setGeneratedContent] = useState('');
  const [selectedTone, setSelectedTone] = useState<string>('');
  const [emotionLevel, setEmotionLevel] = useState<string>('moderate');
  const [isRealTimeMode, setIsRealTimeMode] = useState(false);
  const [showAdvanced, setShowAdvanced] = useState(false);
  const realTimeAnalysisRef = useRef<NodeJS.Timeout | null>(null);

  // Advanced AI Tools Configuration
  const _advancedTools: AdvancedTool[] = [
    {
      id: 'tone-adjustment',
      name: 'Tone Adjustment',
      icon: Palette,
      category: 'tone',
      description: 'Dynamically adjust the emotional tone of your writing',
      requiresSelection: true,
    },
    {
      id: 'pacing-analysis',
      name: 'Pacing Analysis',
      icon: TrendingUp,
      category: 'pacing',
      description: 'Analyze and optimize story rhythm and flow',
      isRealTime: true,
    },
    {
      id: 'emotion-enhancement',
      name: 'Emotion Enhancement',
      icon: Heart,
      category: 'emotion',
      description: 'Deepen emotional resonance and character feelings',
      requiresSelection: true,
    },
    {
      id: 'dialogue-optimization',
      name: 'Dialogue Optimization',
      icon: MessageSquare,
      category: 'dialogue',
      description: 'Improve conversation flow and character voice',
      requiresSelection: true,
    },
    {
      id: 'comprehensive-analysis',
      name: 'Comprehensive Analysis',
      icon: BarChart3,
      category: 'analysis',
      description: 'Complete writing analysis with actionable insights',
      isRealTime: true,
    },
  ];

  // Real-time analysis effect
  useEffect(() => {
    if (isRealTimeMode && currentContent) {
      if (realTimeAnalysisRef.current) {
        clearTimeout(realTimeAnalysisRef.current);
      }

      realTimeAnalysisRef.current = setTimeout(() => {
        performRealTimeAnalysis(currentContent);
      }, 2000); // Debounce for 2 seconds
    }

    return () => {
      if (realTimeAnalysisRef.current) {
        clearTimeout(realTimeAnalysisRef.current);
      }
    };
  }, [currentContent, isRealTimeMode]);

  const performRealTimeAnalysis = useCallback(
    async (text: string) => {
      if (!claudeService.isConfigured() || !text.trim()) return;

      setIsAnalyzing(true);

      try {
        const analysisPrompt = buildComprehensiveAnalysisPrompt(text, sceneTitle, projectContext);
        const response = await claudeService.sendMessage(analysisPrompt, {
          maxTokens: 3000,
        });

        const results = parseAnalysisResponse(response.content);
        setAnalysisResults((prev) => ({
          ...prev,
          [activeCategory]: results,
        }));
      } catch (error) {
        console.error('Real-time analysis failed:', error);
      } finally {
        setIsAnalyzing(false);
      }
    },
    [sceneTitle, projectContext, activeCategory],
  );

  const handleToneAdjustment = useCallback(
    async (targetTone: string) => {
      if (!selectedText || !claudeService.isConfigured()) {
        showToast('Please select text and ensure Claude is configured', 'error');
        return;
      }

      setIsAnalyzing(true);

      try {
        const tonePrompt = buildToneAdjustmentPrompt(selectedText, targetTone, sceneTitle);
        const response = await claudeService.sendMessage(tonePrompt, {
          maxTokens: 2000,
        });

        const result = parseToneAdjustmentResponse(response.content);
        setGeneratedContent(result.adjustedText);
        showToast(`Tone adjusted to ${targetTone}`, 'success');
      } catch (error) {
        console.error('Tone adjustment failed:', error);
        showToast('Tone adjustment failed', 'error');
      } finally {
        setIsAnalyzing(false);
      }
    },
    [selectedText, sceneTitle, showToast],
  );

  const handleEmotionEnhancement = useCallback(
    async (intensity: string) => {
      if (!selectedText || !claudeService.isConfigured()) {
        showToast('Please select text and ensure Claude is configured', 'error');
        return;
      }

      setIsAnalyzing(true);

      try {
        const emotionPrompt = buildEmotionEnhancementPrompt(selectedText, intensity, sceneTitle);
        const response = await claudeService.sendMessage(emotionPrompt, {
          maxTokens: 2000,
        });

        setGeneratedContent(response.content);
        showToast('Emotional depth enhanced', 'success');
      } catch (error) {
        console.error('Emotion enhancement failed:', error);
        showToast('Emotion enhancement failed', 'error');
      } finally {
        setIsAnalyzing(false);
      }
    },
    [selectedText, sceneTitle, showToast],
  );

  const handleDialogueOptimization = useCallback(async () => {
    if (!selectedText || !claudeService.isConfigured()) {
      showToast('Please select dialogue text and ensure Claude is configured', 'error');
      return;
    }

    setIsAnalyzing(true);

    try {
      const dialoguePrompt = buildDialogueOptimizationPrompt(selectedText, projectContext);
      const response = await claudeService.sendMessage(dialoguePrompt, {
        maxTokens: 2500,
      });

      setGeneratedContent(response.content);
      showToast('Dialogue optimized', 'success');
    } catch (error) {
      console.error('Dialogue optimization failed:', error);
      showToast('Dialogue optimization failed', 'error');
    } finally {
      setIsAnalyzing(false);
    }
  }, [selectedText, projectContext, showToast]);

  const handlePacingAnalysis = useCallback(async () => {
    if (!currentContent || !claudeService.isConfigured()) {
      showToast('Please ensure there is content to analyze and Claude is configured', 'error');
      return;
    }

    setIsAnalyzing(true);

    try {
      const pacingPrompt = buildPacingAnalysisPrompt(currentContent, sceneTitle);
      const response = await claudeService.sendMessage(pacingPrompt, {
        maxTokens: 2500,
      });

      const results = parseAnalysisResponse(response.content);
      setAnalysisResults((prev) => ({
        ...prev,
        pacing: results,
      }));

      showToast('Pacing analysis complete', 'success');
    } catch (error) {
      console.error('Pacing analysis failed:', error);
      showToast('Pacing analysis failed', 'error');
    } finally {
      setIsAnalyzing(false);
    }
  }, [currentContent, sceneTitle, showToast]);

  const handleApplyGeneration = useCallback(
    (replaceSelection = false) => {
      if (!onInsertText || !generatedContent) return;

      onInsertText(generatedContent, replaceSelection);
      setGeneratedContent('');
      showToast('Content applied to editor', 'success');
    },
    [onInsertText, generatedContent, showToast],
  );

  const renderToneAdjustmentPanel = () => (
    <div className="space-y-4">
      <div>
        <h4 className="font-medium mb-3">Select Target Tone</h4>
        <div className="grid grid-cols-2 gap-2">
          {TONE_OPTIONS.map((tone) => (
            <button
              key={tone.id}
              onClick={() => {
                setSelectedTone(tone.id);
                handleToneAdjustment(tone.id);
              }}
              disabled={isAnalyzing}
              className={`p-3 rounded-lg border text-left transition-all ${
                selectedTone === tone.id
                  ? 'border-blue-500 bg-blue-50'
                  : 'border-gray-200 hover:border-gray-300'
              } ${isAnalyzing ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              <div className="font-medium text-sm">{tone.label}</div>
              <div className="text-xs text-gray-600 mt-1">{tone.description}</div>
            </button>
          ))}
        </div>
      </div>

      {selectedText && (
        <div className="bg-gray-50 rounded-lg p-3">
          <div className="text-xs text-gray-600 mb-2">Selected Text:</div>
          <div className="text-sm font-mono">
            "{selectedText.slice(0, 150)}
            {selectedText.length > 150 ? '...' : ''}"
          </div>
        </div>
      )}
    </div>
  );

  const renderEmotionEnhancementPanel = () => (
    <div className="space-y-4">
      <div>
        <h4 className="font-medium mb-3">Emotional Intensity Level</h4>
        <div className="space-y-2">
          {EMOTION_LEVELS.map((level) => (
            <button
              key={level.id}
              onClick={() => {
                setEmotionLevel(level.id);
                handleEmotionEnhancement(level.id);
              }}
              disabled={isAnalyzing}
              className={`w-full p-3 rounded-lg border text-left transition-all ${
                emotionLevel === level.id
                  ? 'border-rose-500 bg-rose-50'
                  : 'border-gray-200 hover:border-gray-300'
              } ${isAnalyzing ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              <div className="flex items-center justify-between">
                <span className="font-medium">{level.label}</span>
                <div className="flex gap-1">
                  {Array.from({ length: 4 }, (_, i) => (
                    <div
                      key={i}
                      className={`w-2 h-2 rounded-full ${
                        i < level.intensity ? 'bg-rose-500' : 'bg-gray-300'
                      }`}
                    />
                  ))}
                </div>
              </div>
            </button>
          ))}
        </div>
      </div>

      <div className="bg-amber-50 border border-amber-200 rounded-lg p-3">
        <div className="flex items-start gap-2">
          <Lightbulb className="w-4 h-4 text-amber-600 mt-0.5 flex-shrink-0" />
          <div className="text-sm text-amber-800">
            <strong>Tip:</strong> Higher intensity levels add more emotional language, internal
            thoughts, and physical reactions to convey deeper feelings.
          </div>
        </div>
      </div>
    </div>
  );

  const renderPacingAnalysisPanel = () => {
    const pacingResults = analysisResults.pacing;

    return (
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <h4 className="font-medium">Pacing Analysis</h4>
          <div className="flex items-center gap-2">
            <button
              onClick={() => setIsRealTimeMode(!isRealTimeMode)}
              className={`flex items-center gap-2 px-3 py-1 rounded-lg text-sm transition-colors ${
                isRealTimeMode ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-600'
              }`}
            >
              {isRealTimeMode ? <Pause className="w-3 h-3" /> : <Play className="w-3 h-3" />}
              Real-time
            </button>
            <button
              onClick={handlePacingAnalysis}
              disabled={isAnalyzing}
              className="flex items-center gap-2 px-3 py-1 bg-blue-100 hover:bg-blue-200 text-blue-800 rounded-lg text-sm transition-colors disabled:opacity-50"
            >
              <RefreshCw className={`w-3 h-3 ${isAnalyzing ? 'animate-spin' : ''}`} />
              Analyze
            </button>
          </div>
        </div>

        {pacingResults && (
          <div className="space-y-3">
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-600">Pacing Score:</span>
              <div className="flex-1 bg-gray-200 rounded-full h-2">
                <div
                  className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                  style={{ width: `${pacingResults.score}%` }}
                />
              </div>
              <span className="text-sm font-mono">{pacingResults.score}/100</span>
            </div>

            {pacingResults.issues.length > 0 && (
              <div>
                <h5 className="text-sm font-medium text-red-600 mb-2">Issues Found:</h5>
                <ul className="space-y-1">
                  {pacingResults.issues.map((issue, index) => (
                    <li key={index} className="text-sm text-red-700 flex items-start gap-2">
                      <span className="w-1 h-1 rounded-full bg-red-500 mt-2 flex-shrink-0" />
                      {issue}
                    </li>
                  ))}
                </ul>
              </div>
            )}

            {pacingResults.suggestions.length > 0 && (
              <div>
                <h5 className="text-sm font-medium text-blue-600 mb-2">Suggestions:</h5>
                <ul className="space-y-1">
                  {pacingResults.suggestions.map((suggestion, index) => (
                    <li key={index} className="text-sm text-blue-700 flex items-start gap-2">
                      <span className="w-1 h-1 rounded-full bg-blue-500 mt-2 flex-shrink-0" />
                      {suggestion}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}

        {!pacingResults && !isAnalyzing && (
          <div className="text-center py-8 text-gray-500">
            <TrendingUp className="w-8 h-8 mx-auto mb-2 opacity-50" />
            <p className="text-sm">Click "Analyze" to get pacing insights</p>
          </div>
        )}
      </div>
    );
  };

  const renderDialogueOptimizationPanel = () => (
    <div className="space-y-4">
      <div>
        <h4 className="font-medium mb-3">Dialogue Optimization</h4>
        <p className="text-sm text-gray-600 mb-4">
          Improve conversation flow, character voice, and dialogue authenticity
        </p>
        <button
          onClick={handleDialogueOptimization}
          disabled={isAnalyzing || !selectedText}
          className="w-full flex items-center justify-center gap-2 px-4 py-3 bg-indigo-100 hover:bg-indigo-200 text-indigo-800 rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <MessageSquare className={`w-4 h-4 ${isAnalyzing ? 'animate-pulse' : ''}`} />
          {isAnalyzing ? 'Optimizing...' : 'Optimize Selected Dialogue'}
        </button>
      </div>

      {!selectedText && (
        <div className="bg-amber-50 border border-amber-200 rounded-lg p-3">
          <div className="text-sm text-amber-800">
            Select dialogue text in your editor to optimize character conversations
          </div>
        </div>
      )}

      <div className="space-y-2">
        <h5 className="text-sm font-medium">Optimization Focus Areas:</h5>
        <ul className="space-y-1 text-sm text-gray-600">
          <li className="flex items-center gap-2">
            <Check className="w-3 h-3 text-green-500" />
            Character voice consistency
          </li>
          <li className="flex items-center gap-2">
            <Check className="w-3 h-3 text-green-500" />
            Natural conversation flow
          </li>
          <li className="flex items-center gap-2">
            <Check className="w-3 h-3 text-green-500" />
            Subtext and tension
          </li>
          <li className="flex items-center gap-2">
            <Check className="w-3 h-3 text-green-500" />
            Dialogue tags and action beats
          </li>
        </ul>
      </div>
    </div>
  );

  const renderGeneratedContent = () => {
    if (!generatedContent) return null;

    return (
      <div className="mt-4 border-t pt-4">
        <div className="flex items-center justify-between mb-3">
          <h5 className="font-medium text-sm">Generated Content</h5>
          <div className="flex gap-2">
            <button
              onClick={() => handleApplyGeneration(false)}
              className="flex items-center gap-1 px-3 py-1 bg-green-100 hover:bg-green-200 text-green-800 rounded text-xs transition-colors"
            >
              <Sparkles className="w-3 h-3" />
              Insert
            </button>
            <button
              onClick={() => handleApplyGeneration(true)}
              className="flex items-center gap-1 px-3 py-1 bg-blue-100 hover:bg-blue-200 text-blue-800 rounded text-xs transition-colors"
            >
              <RefreshCw className="w-3 h-3" />
              Replace
            </button>
            <button
              onClick={() => setGeneratedContent('')}
              className="flex items-center gap-1 px-3 py-1 bg-gray-100 hover:bg-gray-200 text-gray-600 rounded text-xs transition-colors"
            >
              <X className="w-3 h-3" />
            </button>
          </div>
        </div>
        <div className="bg-gray-50 rounded-lg p-3 text-sm max-h-40 overflow-y-auto">
          {generatedContent}
        </div>
      </div>
    );
  };

  return (
    <div className={`bg-white border border-gray-200 rounded-lg shadow-lg ${className}`}>
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b border-gray-200">
        <h3 className="font-semibold text-lg">Enhanced AI Toolkit</h3>
        <div className="flex items-center gap-2">
          <button
            onClick={() => setShowAdvanced(!showAdvanced)}
            className="p-1 hover:bg-gray-100 rounded transition-colors"
          >
            <Settings className="w-4 h-4" />
          </button>
          {position === 'popup' && onClose && (
            <button onClick={onClose} className="p-1 hover:bg-gray-100 rounded transition-colors">
              <X className="w-4 h-4" />
            </button>
          )}
        </div>
      </div>

      {/* Category Navigation */}
      <div className="flex border-b border-gray-200">
        {[
          { id: 'tone', label: 'Tone', icon: Palette },
          { id: 'emotion', label: 'Emotion', icon: Heart },
          { id: 'pacing', label: 'Pacing', icon: TrendingUp },
          { id: 'dialogue', label: 'Dialogue', icon: MessageSquare },
        ].map(({ id, label, icon: Icon }) => (
          <button
            key={id}
            onClick={() => setActiveCategory(id as any)}
            className={`flex-1 flex items-center justify-center gap-2 py-3 px-4 text-sm font-medium transition-colors ${
              activeCategory === id
                ? 'text-blue-600 border-b-2 border-blue-600'
                : 'text-gray-600 hover:text-gray-900'
            }`}
          >
            <Icon className="w-4 h-4" />
            {label}
          </button>
        ))}
      </div>

      {/* Content Panel */}
      <div className="p-4">
        {isAnalyzing && (
          <div className="flex items-center gap-2 mb-4 text-blue-600">
            <Brain className="w-4 h-4 animate-pulse" />
            <span className="text-sm">AI is analyzing...</span>
          </div>
        )}

        {activeCategory === 'tone' && renderToneAdjustmentPanel()}
        {activeCategory === 'emotion' && renderEmotionEnhancementPanel()}
        {activeCategory === 'pacing' && renderPacingAnalysisPanel()}
        {activeCategory === 'dialogue' && renderDialogueOptimizationPanel()}

        {renderGeneratedContent()}
      </div>
    </div>
  );
}

// AI Prompt Building Functions
function buildToneAdjustmentPrompt(text: string, targetTone: string, sceneTitle: string): string {
  const toneDescriptions = {
    mysterious: 'dark, suspenseful, intriguing with hidden meanings',
    romantic: 'warm, intimate, emotional with tender feelings',
    action: 'fast-paced, intense, dynamic with urgency',
    comedic: 'light, humorous, entertaining with wit',
    dramatic: 'serious, emotional, impactful with gravitas',
    contemplative: 'thoughtful, reflective, deep with introspection',
    tense: 'anxious, suspenseful, on-edge with unease',
    melancholic: 'sad, wistful, bittersweet with longing',
  };

  return `You are a tone adjustment specialist. Rewrite the following text to match the target tone while preserving the core meaning and narrative function.

SCENE CONTEXT: ${sceneTitle}
TARGET TONE: ${targetTone} (${toneDescriptions[targetTone as keyof typeof toneDescriptions] || targetTone})

ORIGINAL TEXT:
"${text}"

REQUIREMENTS:
- Maintain the essential plot points and character actions
- Adjust vocabulary, sentence structure, and imagery to match the target tone
- Ensure the new tone feels natural and not forced
- Keep the same general length as the original

Respond with only the rewritten text, maintaining proper formatting.`;
}

function buildEmotionEnhancementPrompt(
  text: string,
  intensity: string,
  sceneTitle: string,
): string {
  const intensityGuidance = {
    subtle: 'Add gentle emotional undertones without overwhelming the narrative',
    moderate: 'Include clear emotional depth with internal thoughts and reactions',
    strong: 'Emphasize emotions with vivid descriptions and character vulnerability',
    intense: 'Create powerful emotional impact with visceral reactions and deep introspection',
  };

  return `You are an emotional depth specialist. Enhance the following text with deeper emotional resonance while maintaining narrative flow.

SCENE CONTEXT: ${sceneTitle}
INTENSITY LEVEL: ${intensity}
GUIDANCE: ${intensityGuidance[intensity as keyof typeof intensityGuidance]}

ORIGINAL TEXT:
"${text}"

ENHANCEMENT TECHNIQUES:
- Add internal emotional responses and thoughts
- Include physical manifestations of emotions
- Deepen character vulnerability and authenticity
- Use sensory details that convey emotional states
- Maintain the original narrative structure

Respond with the enhanced text, showing not telling the emotions.`;
}

function buildPacingAnalysisPrompt(content: string, sceneTitle: string): string {
  return `You are a pacing analysis expert. Analyze the following scene for rhythm, flow, and dramatic timing.

SCENE: ${sceneTitle}

CONTENT TO ANALYZE:
"${content.slice(0, 2000)}${content.length > 2000 ? '...' : ''}"

Provide analysis in this JSON format:
{
  "score": 85,
  "issues": ["Long paragraphs slow down action scenes", "Dialogue lacks urgency"],
  "suggestions": ["Break up descriptive passages", "Add shorter, punchier dialogue"],
  "improvements": ["Consider faster transitions", "Vary sentence length for rhythm"],
  "confidence": 0.85
}

Focus on:
- Sentence variety and rhythm
- Paragraph length and structure
- Dialogue pacing and flow
- Action vs. reflection balance
- Tension building and release
- Scene transitions`;
}

function buildDialogueOptimizationPrompt(text: string, projectContext: string): string {
  return `You are a dialogue optimization expert. Improve the following dialogue for natural flow, character voice, and conversational authenticity.

PROJECT CONTEXT: ${projectContext}

DIALOGUE TO OPTIMIZE:
"${text}"

OPTIMIZATION FOCUS:
- Make conversation feel natural and authentic
- Ensure each character has a distinct voice
- Add appropriate subtext and tension
- Balance dialogue tags with action beats
- Remove unnecessary exposition in dialogue
- Enhance emotional resonance

Respond with the optimized dialogue, maintaining character consistency and scene purpose.`;
}

function buildComprehensiveAnalysisPrompt(
  content: string,
  sceneTitle: string,
  projectContext: string,
): string {
  return `You are a comprehensive writing analyst. Analyze this scene for overall quality and provide actionable feedback.

SCENE: ${sceneTitle}
PROJECT: ${projectContext}

CONTENT:
"${content.slice(0, 1500)}${content.length > 1500 ? '...' : ''}"

Provide analysis in this JSON format:
{
  "score": 78,
  "issues": ["Pacing could be faster", "Character motivation unclear"],
  "suggestions": ["Add more sensory details", "Strengthen dialogue"],
  "improvements": ["Consider shorter paragraphs", "Enhance emotional depth"],
  "confidence": 0.82
}

Analyze for: pacing, character development, dialogue quality, emotional depth, clarity, and engagement.`;
}

// Response Parsing Functions
function parseAnalysisResponse(response: string): AIAnalysisResult {
  try {
    const jsonMatch = response.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      const parsed = JSON.parse(jsonMatch[0]);
      return {
        score: parsed.score || 0,
        issues: parsed.issues || [],
        suggestions: parsed.suggestions || [],
        improvements: parsed.improvements || [],
        confidence: parsed.confidence || 0.5,
      };
    }
  } catch (error) {
    console.error('Failed to parse analysis response:', error);
  }

  return {
    score: 0,
    issues: ['Analysis parsing failed'],
    suggestions: ['Please try again'],
    improvements: [],
    confidence: 0,
  };
}

function parseToneAdjustmentResponse(response: string): ToneAdjustmentResult {
  // Simple parsing - in a real implementation, you might want more sophisticated parsing
  return {
    originalTone: 'original',
    newTone: 'adjusted',
    adjustedText: response,
    explanation: 'Tone successfully adjusted',
  };
}

// src/components/Writing/EnhancedWritingEditor.tsx - Fixed version with consistency checking
import CharacterCount from '@tiptap/extension-character-count';
import Placeholder from '@tiptap/extension-placeholder';
import Typography from '@tiptap/extension-typography';
import { useEditor, EditorContent } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';
import {
  Save,
  Eye,
  EyeOff,
  Target,
  Clock,
  PanelLeftOpen,
  PanelLeftClose,
  Focus,
  Bot,
  Brain,
  FileText,
  AlertTriangle,
} from 'lucide-react';
import React, { useState, useEffect, useRef, useCallback } from 'react';

import { useToast } from '@/context/toast';
import type { EnhancedProject } from '@/types/project';
import { triggerWritingPanelOpen } from '@/utils/tourTriggers';

import { useAppContext } from '../../context/AppContext';
import { storageService } from '../../services/storageService';
import { Scene, Chapter } from '../../types/writing';
import { debounce as debounceUtil } from '../../utils/debounce';
import { _focusWritingEditor as focusWritingEditor } from '../../utils/focusUtils';
import ConsistencyIssuesPanel from '../editor/ConsistencyIssuesPanel';
import { ConsistencyExtension } from '../editor/extensions/ConsistencyExtension';

import ClaudeToolbar from './ClaudeToolbar';
import EnhancedAIWritingToolbar from './EnhancedAIWritingToolbar';
import SceneNavigationPanel from './SceneNavigationPanel';

import type {
  EditorIssue,
  ConsistencyDecorationOptions,
} from '../../services/editorConsistencyDecorator';

// Import the consistency styles
import '../../styles/consistency-issues.css';

interface EnhancedWritingEditorProps {
  className?: string;
}

const EnhancedWritingEditor: React.FC<EnhancedWritingEditorProps> = ({ className = '' }) => {
  const { currentProject, state } = useAppContext();
  const { showToast } = useToast();

  // State
  const [currentScene, setCurrentScene] = useState<Scene | null>(null);
  const [currentChapter, setCurrentChapter] = useState<Chapter | null>(null);
  const [showScenePanel, setShowScenePanel] = useState(true);
  const [showAIPanel, setShowAIPanel] = useState(true);
  const [showEnhancedToolbar, setShowEnhancedToolbar] = useState(false);
  const [showConsistencyPanel, setShowConsistencyPanel] = useState(false);
  const [isFocusMode, setIsFocusMode] = useState(false);
  const [showWordGoal, setShowWordGoal] = useState(false);
  const [wordGoal, _setWordGoal] = useState(500);
  const [selectedText, setSelectedText] = useState('');
  const [toolbarPosition, setToolbarPosition] = useState({ x: 0, y: 0 });
  const [showPopupToolbar, setShowPopupToolbar] = useState(false);
  const [lastSaved, setLastSaved] = useState<Date | null>(null);
  const [manuscriptPreview, setManuscriptPreview] = useState(false);

  // Consistency checking state
  const [consistencyEnabled, setConsistencyEnabled] = useState(true);
  const [consistencyIssues, setConsistencyIssues] = useState<EditorIssue[]>([]);
  const [consistencyOptions, setConsistencyOptions] = useState<
    Partial<ConsistencyDecorationOptions>
  >({});

  const editorRef = useRef<HTMLDivElement>(null);

  // Consistency extension handlers
  const handleConsistencyIssuesUpdated = useCallback((issues: EditorIssue[]) => {
    setConsistencyIssues(issues);
  }, []);

  // TipTap Editor
  const editor = useEditor({
    extensions: [
      StarterKit,
      Placeholder.configure({
        placeholder: ({ node }) => {
          if (node.type.name === 'heading') {
            return "What's the title?";
          }
          return 'Start writing your scene...';
        },
      }),
      CharacterCount.configure({
        limit: 100000,
      }),
      Typography,
      ConsistencyExtension.configure({
        project: currentProject as EnhancedProject | null,
        scene: currentScene,
        chapter: currentChapter,
        enabled: consistencyEnabled,
        decorationOptions: consistencyOptions,
        onIssuesUpdated: handleConsistencyIssuesUpdated,
        onIssueClicked: (issue: EditorIssue) => {
          if (editor) {
            editor.chain().focus().setTextSelection(issue.startPos).run();
            if (!showConsistencyPanel) {
              setShowConsistencyPanel(true);
            }
          }
        },
      }),
    ],
    content: currentScene?.content || '',
    onUpdate: ({ editor }) => {
      const content = editor.getHTML();
      const text = editor.getText();
      const wordCount = text.split(/\s+/).filter((word) => word.length > 0).length;

      // Update current scene content
      if (currentScene) {
        const updatedScene = {
          ...currentScene,
          content,
          wordCount,
          updatedAt: new Date(),
        };
        setCurrentScene(updatedScene);

        // Auto-save
        debouncedSave(updatedScene);
      }
    },
    onSelectionUpdate: ({ editor }) => {
      const { from, to } = editor.state.selection;
      const text = editor.state.doc.textBetween(from, to, ' ');

      if (text.trim() && text.length > 10) {
        setSelectedText(text);

        // Get cursor position for popup toolbar
        const { view } = editor;
        const start = view.coordsAtPos(from);
        setToolbarPosition({
          x: start.left,
          y: start.top - 80,
        });

        // Show popup toolbar after a short delay to avoid flickering
        setTimeout(() => setShowPopupToolbar(true), 300);
      } else {
        setSelectedText('');
        setShowPopupToolbar(false);
      }
    },
    editorProps: {
      attributes: {
        class: `
          prose prose-lg max-w-none focus:outline-none
          bg-white text-gray-900
          prose-headings:font-bold
          prose-p:leading-relaxed
          prose-strong:text-gray-900
          prose-em:text-gray-700
        `,
        'data-testid': 'writing-editor',
      },
    },
    immediatelyRender: false,
  });

  // Consistency issue click handler
  const handleConsistencyIssueClick = useCallback(
    (issue: EditorIssue) => {
      if (editor) {
        // Jump to the issue position in the editor
        editor.chain().focus().setTextSelection(issue.startPos).run();

        // Show the consistency panel if not already visible
        if (!showConsistencyPanel) {
          setShowConsistencyPanel(true);
        }
      }
    },
    [editor, showConsistencyPanel],
  );

  // Auto-save functionality
  const saveScene = useCallback(
    async (scene: Scene) => {
      if (!currentProject) return;

      try {
        await storageService.saveScene(currentProject.id, scene);
        setLastSaved(new Date());
      } catch (error) {
        console.error('Failed to save scene:', error);
        showToast('Failed to save scene', 'error');
      }
    },
    [currentProject, showToast],
  );

  // Debounced save function
  const debouncedSave = useCallback(
    debounceUtil((scene: Scene) => saveScene(scene), 2000),
    [saveScene],
  );

  // Load initial scene
  useEffect(() => {
    const loadInitialScene = async () => {
      if (!currentProject) return;

      try {
        const chapters = await storageService.loadWritingChapters(currentProject.id);
        if (chapters.length > 0) {
          const firstChapter = chapters[0];
          if (firstChapter && firstChapter.scenes.length > 0) {
            const firstScene = firstChapter.scenes[0];
            if (firstScene) {
              setCurrentScene(firstScene);
              setCurrentChapter(firstChapter);
              editor?.commands.setContent(firstScene.content || '');

              // Trigger tour event for writing panel open (first time only)
              queueMicrotask(() => {
                try {
                  triggerWritingPanelOpen(currentProject.id);
                } catch (error) {
                  // Silent fail - tour triggers should not break functionality
                  if (import.meta.env.DEV) {
                    console.warn('[EnhancedWritingEditor] Tour trigger failed:', error);
                  }
                }
              });
            }
          }
        }
      } catch (error) {
        console.error('Failed to load scenes:', error);
        showToast('Failed to load scenes', 'error');
      }
    };

    loadInitialScene();
  }, [currentProject, editor, showToast]);

  // Update consistency extension when context changes
  useEffect(() => {
    if (editor && currentProject && currentScene && currentChapter) {
      // Convert Project to EnhancedProject
      const enhancedProject: EnhancedProject = {
        ...currentProject,
        currentWordCount: 0,
        plotNotes: [],
        worldBuilding: [],
        recentContent: '',
        sessions: [],
        claudeContext: {
          includeCharacters: true,
          includePlotNotes: true,
          includeWorldBuilding: true,
          maxCharacters: 5,
          maxPlotNotes: 10,
          contextLength: 'medium',
        },
      };

      editor.commands.updateConsistencyContext(enhancedProject, currentScene, currentChapter);
    }
  }, [editor, currentProject, currentScene, currentChapter]);

  // Update consistency extension options
  useEffect(() => {
    if (editor) {
      editor.commands.updateDecorationOptions(consistencyOptions);
    }
  }, [editor, consistencyOptions]);

  // Consistency handlers
  const handleToggleConsistency = useCallback(
    (enabled: boolean) => {
      setConsistencyEnabled(enabled);
      if (editor) {
        editor.commands.toggleConsistencyChecking();
      }
    },
    [editor],
  );

  const handleUpdateConsistencyOptions = useCallback(
    (options: Partial<ConsistencyDecorationOptions>) => {
      setConsistencyOptions((prev) => ({
        ...prev,
        ...options,
      }));
    },
    [],
  );

  const handleRefreshConsistencyAnalysis = useCallback(() => {
    if (editor && currentProject && currentScene && currentChapter) {
      // Convert Project to EnhancedProject
      const enhancedProject: EnhancedProject = {
        ...currentProject,
        currentWordCount: 0,
        plotNotes: [],
        worldBuilding: [],
        recentContent: '',
        sessions: [],
        claudeContext: {
          includeCharacters: true,
          includePlotNotes: true,
          includeWorldBuilding: true,
          maxCharacters: 5,
          maxPlotNotes: 10,
          contextLength: 'medium',
        },
      };

      editor.commands.updateConsistencyContext(enhancedProject, currentScene, currentChapter);

      showToast('Consistency analysis refreshed', 'success');
    }
  }, [editor, currentProject, currentScene, currentChapter, showToast]);

  // Handle scene selection
  const handleSceneSelect = (scene: Scene, chapter: Chapter) => {
    // Save current scene first
    if (currentScene && editor) {
      const content = editor.getHTML();
      const text = editor.getText();
      const wordCount = text.split(/\s+/).filter((word: string) => word.length > 0).length;

      const updatedScene = {
        ...currentScene,
        content,
        wordCount,
        updatedAt: new Date(),
      };

      saveScene(updatedScene);
    }

    // Switch to new scene
    setCurrentScene(scene);
    setCurrentChapter(chapter);
    editor?.commands.setContent(scene.content || '');

    // Focus editor
    setTimeout(() => {
      focusWritingEditor();
    }, 100);

    showToast(`Switched to ${scene.title}`, 'success');
  };

  // Handle scene creation
  const handleSceneCreate = (_chapterId: string) => {
    // Refresh scene list by reloading
    showToast('Scene created! Refreshing...', 'success');
  };

  // AI Toolbar handlers
  const handleInsertText = (text: string, replaceSelection: boolean = false) => {
    if (!editor) return;

    if (replaceSelection && selectedText) {
      const { from, to } = editor.state.selection;
      editor.chain().focus().deleteRange({ from, to }).insertContent(text).run();
    } else {
      editor.chain().focus().insertContent(`\n\n${text}`).run();
    }

    setShowPopupToolbar(false);
    setSelectedText('');
  };

  // Manual save
  const handleManualSave = async () => {
    if (!currentScene || !editor) return;

    const content = editor.getHTML();
    const text = editor.getText();
    const wordCount = text.split(/\s+/).filter((word: string) => word.length > 0).length;

    const updatedScene = {
      ...currentScene,
      content,
      wordCount,
      updatedAt: new Date(),
    };

    await saveScene(updatedScene);
    showToast('Scene saved', 'success');
  };

  // Focus mode toggle
  const toggleFocusMode = () => {
    setIsFocusMode(!isFocusMode);
    if (!isFocusMode) {
      showToast('Focus mode enabled', 'success');
    } else {
      showToast('Focus mode disabled', 'success');
    }
  };

  // Calculate word goal progress
  const wordCount = currentScene?.wordCount || 0;
  const wordGoalProgress = Math.min((wordCount / wordGoal) * 100, 100);

  if (!currentProject) {
    return (
      <div className="h-full flex items-center justify-center text-gray-500">
        <div className="text-center">
          <h3 className="text-lg font-medium mb-2">No Project Selected</h3>
          <p className="text-sm">Select a project to start writing</p>
        </div>
      </div>
    );
  }

  return (
    <div
      data-tour="editor-root"
      className={`h-full flex ${isFocusMode ? 'bg-gray-50' : ''} ${className}`}
    >
      {/* Scene Navigation Panel */}
      {showScenePanel && !isFocusMode && (
        <div className="w-80 border-r border-gray-200 bg-white">
          <SceneNavigationPanel
            currentSceneId={currentScene?.id}
            onSceneSelect={handleSceneSelect}
            onSceneCreate={handleSceneCreate}
            className="h-full"
          />
        </div>
      )}

      {/* Main Editor Area */}
      <div className="flex-1 flex flex-col">
        {/* Editor Toolbar */}
        {!isFocusMode && (
          <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-white">
            <div className="flex items-center space-x-4">
              {/* Panel toggles */}
              <button
                onClick={() => setShowScenePanel(!showScenePanel)}
                className="p-2 hover:bg-gray-100 rounded text-gray-600"
                title={showScenePanel ? 'Hide scene panel' : 'Show scene panel'}
              >
                {showScenePanel ? <PanelLeftClose size={18} /> : <PanelLeftOpen size={18} />}
              </button>

              <button
                onClick={() => setShowAIPanel(!showAIPanel)}
                className="p-2 hover:bg-gray-100 rounded text-gray-600"
                title={showAIPanel ? 'Hide AI panel' : 'Show AI panel'}
              >
                <Bot size={18} />
              </button>

              <button
                onClick={() => setShowEnhancedToolbar(!showEnhancedToolbar)}
                className={`p-2 hover:bg-gray-100 rounded transition-colors ${
                  showEnhancedToolbar ? 'text-blue-600 bg-blue-50' : 'text-gray-600'
                }`}
                title={
                  showEnhancedToolbar ? 'Hide enhanced AI toolkit' : 'Show enhanced AI toolkit'
                }
              >
                <Brain size={18} />
              </button>

              <button
                onClick={() => setShowConsistencyPanel(!showConsistencyPanel)}
                className={`relative p-2 hover:bg-gray-100 rounded transition-colors ${
                  consistencyIssues.length > 0 && consistencyEnabled
                    ? 'text-orange-600'
                    : 'text-gray-600'
                }`}
                title={showConsistencyPanel ? 'Hide consistency panel' : 'Show consistency panel'}
              >
                <AlertTriangle size={18} />
                {consistencyIssues.length > 0 && consistencyEnabled && (
                  <span className="absolute -top-1 -right-1 bg-orange-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                    {consistencyIssues.length > 9 ? '9+' : consistencyIssues.length}
                  </span>
                )}
              </button>

              {/* Current scene info */}
              {currentScene && (
                <div className="flex items-center space-x-2">
                  <h2 className="font-medium text-gray-900">{currentScene.title}</h2>
                  <span className="text-sm text-gray-500">in {currentChapter?.title}</span>
                </div>
              )}
            </div>

            <div className="flex items-center space-x-2">
              {/* Word count and goal */}
              <div className="flex items-center space-x-3 text-sm text-gray-600">
                <div className="flex items-center space-x-1">
                  <Clock size={14} />
                  <span>{wordCount.toLocaleString()} words</span>
                </div>

                {showWordGoal && (
                  <div className="flex items-center space-x-2">
                    <Target size={14} />
                    <div className="w-16 h-2 bg-gray-200 rounded-full overflow-hidden">
                      <div
                        className="h-full bg-blue-500 transition-all duration-300"
                        style={{ width: `${wordGoalProgress}%` }}
                      />
                    </div>
                    <span className="text-xs">{wordGoal}</span>
                  </div>
                )}

                <button
                  onClick={() => setShowWordGoal(!showWordGoal)}
                  className="p-1 hover:bg-gray-100 rounded"
                  title="Toggle word goal"
                >
                  {showWordGoal ? <EyeOff size={14} /> : <Eye size={14} />}
                </button>
              </div>

              {/* Save indicator */}
              {state.autoSave.isSaving ? (
                <div className="flex items-center space-x-1 text-sm text-blue-600">
                  <div className="animate-spin rounded-full h-3 w-3 border border-blue-600 border-t-transparent"></div>
                  <span>Saving...</span>
                </div>
              ) : lastSaved ? (
                <div className="text-sm text-green-600">Saved {formatTime(lastSaved)}</div>
              ) : null}

              {/* Manual save */}
              <button
                onClick={handleManualSave}
                className="p-2 hover:bg-gray-100 rounded text-gray-600"
                title="Save scene"
              >
                <Save size={16} />
              </button>

              {/* Manuscript Preview */}
              <button
                onClick={() => setManuscriptPreview(!manuscriptPreview)}
                className={`p-2 hover:bg-gray-100 rounded transition-colors ${
                  manuscriptPreview ? 'text-blue-600 bg-blue-50' : 'text-gray-600'
                }`}
                title="Toggle manuscript preview"
              >
                <FileText size={16} />
              </button>

              {/* Focus mode */}
              <button
                onClick={toggleFocusMode}
                className="p-2 hover:bg-gray-100 rounded text-gray-600"
                title="Toggle focus mode"
              >
                <Focus size={16} />
              </button>
            </div>
          </div>
        )}

        {/* Editor Content */}
        <div className="flex-1 flex overflow-hidden">
          {/* Main Editor */}
          <div
            className={`flex-1 overflow-y-auto bg-white ${
              showAIPanel || showConsistencyPanel ? 'pr-4' : ''
            } ${manuscriptPreview ? 'bg-white' : ''}`}
          >
            <div
              className={`
                max-w-none mx-auto p-8 bg-white text-gray-900
                ${isFocusMode ? 'max-w-4xl pt-16' : 'max-w-4xl'}
                ${manuscriptPreview ? 'manuscript-preview' : ''}
              `}
            >
              {currentScene ? (
                <div ref={editorRef} className="min-h-full">
                  <EditorContent editor={editor} className="min-h-full focus-within:outline-none" />
                </div>
              ) : (
                <div className="text-center py-16">
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No Scene Selected</h3>
                  <p className="text-gray-600 mb-4">
                    Select a scene from the navigation panel to start writing
                  </p>
                  {!showScenePanel && (
                    <button
                      onClick={() => setShowScenePanel(true)}
                      className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded"
                    >
                      Show Scene Panel
                    </button>
                  )}
                </div>
              )}
            </div>
          </div>

          {/* AI Toolbar Panel */}
          {showAIPanel && !isFocusMode && (
            <div
              data-tour-id="assistant-panel"
              className="w-96 border-l border-gray-200 bg-white overflow-y-auto"
            >
              <div className="p-4">
                <ClaudeToolbar
                  selectedText={selectedText}
                  onInsertText={handleInsertText}
                  sceneTitle={currentScene?.title || ''}
                  currentContent={editor?.getText() || ''}
                  position="panel"
                  className="border-0 bg-transparent"
                />
              </div>
            </div>
          )}

          {/* Enhanced AI Toolkit Panel */}
          {showEnhancedToolbar && !isFocusMode && (
