function gradeClass(g: string) {
  const s = (g || '').toUpperCase();
  return s === 'A' ? 'A' : s === 'B' ? 'B' : s === 'C' ? 'C' : s === 'D' ? 'D' : 'F';
}
function escapeHtml(s: string) {
  return s.replace(
    /[&<>"']/g,
    (m) =>
      ({
        '&': '&amp;',
        '<': '&lt;',
        '>': '&gt;',
        '"': '&quot;',
        "'": '&#039;',
      })[m]!,
  );
}

