// Keyboard navigation and accessibility for Plot Boards
// Provides arrow key navigation, drag-and-drop keyboard controls, and screen reader support

import { useCallback, useEffect, useRef, useState } from 'react';

import { PlotBoard } from '../types';

import type {
  CardPosition,
  KeyboardNavigationState,
  KeyboardNavigationActions as KeyboardNavigationActionsType,
} from '../types/keyboard';

interface _FocusState {
  cardId: string | null;
  columnId: string | null;
}

interface UseKeyboardNavigationProps {
  board: PlotBoard;
  onMoveCard: (_cardId: string, _targetColumnId: string, _newOrder: number) => void;
  onReorderColumns: (_columnIds: string[]) => void;
}

export const useKeyboardNavigation = ({
  board,
  onMoveCard,
  onReorderColumns: _onReorderColumns,
}: UseKeyboardNavigationProps): KeyboardNavigationState & KeyboardNavigationActionsType => {
  const [state, setState] = useState<KeyboardNavigationState>({
    focusedCardId: null,
    focusedColumnId: null,
    draggedCardId: null,
    isDragging: false,
    announcements: [],
  });

  const lastFocusRef = useRef<{ cardId: string | null; columnId: string | null }>({
    cardId: null,
    columnId: null,
  });

  // Get all cards in a flat array with their positions
  const getAllCards = useCallback((): CardPosition[] => {
    const cards: CardPosition[] = [];

    board.columns.forEach((column, columnIndex) => {
      column.cards.forEach((card, cardIndex) => {
        cards.push({ card, column, cardIndex, columnIndex });
      });
    });

    return cards;
  }, [board]);

  // Find card position in the grid
  const findCardPosition = useCallback(
    (cardId: string) => {
      const allCards = getAllCards();
      return allCards.find((item) => item.card.id === cardId);
    },
    [getAllCards],
  );

  // Navigate to adjacent card
  const navigateToCard = useCallback(
    (direction: 'up' | 'down' | 'left' | 'right') => {
      if (!state.focusedCardId) {
        // If no card focused, focus first card
        const allCards = getAllCards();
        if (allCards.length > 0 && allCards[0]) {
          const firstCard = allCards[0];
          setState((prev) => ({
            ...prev,
            focusedCardId: firstCard.card.id,
            focusedColumnId: firstCard.column.id,
          }));
          lastFocusRef.current = {
            cardId: firstCard.card.id,
            columnId: firstCard.column.id,
          };
        }
        return;
      }

      const currentPosition = findCardPosition(state.focusedCardId);
      if (!currentPosition) return;

      const { columnIndex, cardIndex } = currentPosition;
      let targetColumnIndex = columnIndex;
      let targetCardIndex = cardIndex;

      switch (direction) {
        case 'up':
          targetCardIndex = Math.max(0, cardIndex - 1);
          break;
        case 'down':
          const currentColumn = board.columns[columnIndex];
          if (currentColumn && currentColumn.cards) {
            targetCardIndex = Math.min(currentColumn.cards.length - 1, cardIndex + 1);
          }
          break;
        case 'left':
          targetColumnIndex = Math.max(0, columnIndex - 1);
          const leftColumn = board.columns[targetColumnIndex];
          if (leftColumn && leftColumn.cards) {
            targetCardIndex = Math.min(cardIndex, leftColumn.cards.length - 1);
          }
          break;
        case 'right':
          targetColumnIndex = Math.min(board.columns.length - 1, columnIndex + 1);
          const rightColumn = board.columns[targetColumnIndex];
          if (rightColumn && rightColumn.cards) {
            targetCardIndex = Math.min(cardIndex, rightColumn.cards.length - 1);
          }
          break;
      }

      const targetColumn = board.columns[targetColumnIndex];
      if (!targetColumn || !targetColumn.cards) return;

      const targetCard = targetColumn.cards[targetCardIndex];
      if (targetCard) {
        setState((prev) => ({
          ...prev,
          focusedCardId: targetCard.id,
          focusedColumnId: targetColumn.id,
        }));
        lastFocusRef.current = { cardId: targetCard.id, columnId: targetColumn.id };
      }
    },
    [state.focusedCardId, getAllCards, findCardPosition, board],
  );

  // Handle keyboard events
  const handleKeyDown = useCallback(
    (event: KeyboardEvent) => {
      // Don't interfere with input fields
      if (
        event.target instanceof HTMLInputElement ||
        event.target instanceof HTMLTextAreaElement ||
        event.target instanceof HTMLSelectElement
      ) {
        return;
      }

      switch (event.key) {
        case 'ArrowUp':
          event.preventDefault();
          navigateToCard('up');
          break;
        case 'ArrowDown':
          event.preventDefault();
          navigateToCard('down');
          break;
        case 'ArrowLeft':
          event.preventDefault();
          navigateToCard('left');
          break;
        case 'ArrowRight':
          event.preventDefault();
          navigateToCard('right');
          break;
        case 'Enter':
          event.preventDefault();
          if (state.focusedCardId && !state.isDragging) {
            // Open card for editing (could trigger a modal or inline edit)
            announce(`Opening card ${findCardPosition(state.focusedCardId)?.card.title || 'card'}`);
          }
          break;
        case ' ':
        case 'Spacebar':
          event.preventDefault();
          if (!state.isDragging && state.focusedCardId) {
            startDrag(state.focusedCardId);
          } else if (state.isDragging && state.draggedCardId) {
            // Drop at current focus position
            const focusedPosition = state.focusedColumnId
              ? board.columns.find((col) => col.id === state.focusedColumnId)
              : null;
            if (focusedPosition) {
              const newOrder = state.focusedCardId
                ? findCardPosition(state.focusedCardId)?.cardIndex || 0
                : 0;
              completeDrop(state.focusedColumnId!, newOrder);
            }
          }
          break;
        case 'Escape':
          event.preventDefault();
          if (state.isDragging) {
            cancelDrag();
          }
          break;
      }
    },
    [
      state.focusedCardId,
      state.focusedColumnId,
      state.isDragging,
      state.draggedCardId,
      navigateToCard,
      findCardPosition,
      board,
    ],
  );

  // Declare announce function stub to be defined later
  let announce: (message: string) => void;
  let startDrag: (cardId: string) => void;
  let completeDrop: (targetColumnId: string, newOrder: number) => void;
  let cancelDrag: () => void;

  // Set up keyboard event listeners
  useEffect(() => {
    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [handleKeyDown]);

  // Actions
  const setFocus = useCallback((cardId: string | null, columnId: string | null) => {
    setState((prev) => ({ ...prev, focusedCardId: cardId, focusedColumnId: columnId }));
    lastFocusRef.current = { cardId, columnId };
  }, []);

  startDrag = useCallback(
    (cardId: string) => {
      const position = findCardPosition(cardId);
      if (!position) return;

      setState((prev) => ({
        ...prev,
        draggedCardId: cardId,
        isDragging: true,
      }));

      announce(
        `Picked up card: ${position.card.title}. Use arrow keys to navigate, Space to drop, Escape to cancel.`,
      );
    },
    [findCardPosition],
  );

  completeDrop = useCallback(
    (targetColumnId: string, newOrder: number) => {
      if (!state.draggedCardId) return;

      const draggedCard = findCardPosition(state.draggedCardId);
      const targetColumn = board.columns.find((col) => col.id === targetColumnId);

      if (!draggedCard || !targetColumn) return;

      onMoveCard(state.draggedCardId, targetColumnId, newOrder);

      announce(
        `Dropped card: ${draggedCard.card.title} into column: ${targetColumn.title} at position ${newOrder + 1}`,
      );

      setState((prev) => ({
        ...prev,
        draggedCardId: null,
        isDragging: false,
        focusedCardId: prev.draggedCardId, // Keep focus on the moved card
      }));
    },
    [state.draggedCardId, findCardPosition, board, onMoveCard],
  );

  cancelDrag = useCallback(() => {
    if (!state.draggedCardId) return;

    const draggedCard = findCardPosition(state.draggedCardId);
    announce(`Cancelled drag of card: ${draggedCard?.card.title || 'card'}`);

    setState((prev) => ({
      ...prev,
      draggedCardId: null,
      isDragging: false,
      focusedCardId: prev.draggedCardId, // Return focus to original card
    }));
  }, [state.draggedCardId, findCardPosition]);

  announce = useCallback((message: string) => {
    setState((prev) => ({
      ...prev,
      announcements: [...prev.announcements, message],
    }));
  }, []);

  const clearAnnouncements = useCallback(() => {
    setState((prev) => ({ ...prev, announcements: [] }));
  }, []);

  return {
    ...state,
    setFocus,
    startDrag,
    completeDrop,
    cancelDrag,
    announce,
    clearAnnouncements,
  };
};

// Hook for managing ARIA live region announcements
export const useAriaLiveRegion = (announcements: string[]) => {
  const [currentAnnouncement, setCurrentAnnouncement] = useState<string>('');

  useEffect(() => {
    if (announcements.length > 0) {
      const latestAnnouncement = announcements[announcements.length - 1];
      if (latestAnnouncement) {
        setCurrentAnnouncement(latestAnnouncement);

        // Clear announcement after a delay to allow it to be read
        const timer = setTimeout(() => {
          setCurrentAnnouncement('');
        }, 1000);

        return () => clearTimeout(timer);
      }
    }
    return () => {}; // Always return cleanup function
  }, [announcements]);

  return currentAnnouncement;
};

import devLog from "src/utils/devLogger";
import { useCallback, useMemo } from 'react';

import aiService from '../../../services/aiPlotAnalysisService';
import { analyticsService } from '../../../services/analyticsService';
import { featureFlags } from '../../../utils/flags';
import { usePlotBoardStore } from '../store';

export function usePlotAnalysis(profileId: string, projectId: string) {
  const { boards, activeBoard, analysisByProjectId, setAnalysis } = usePlotBoardStore();

  const analysis = analysisByProjectId[projectId];

  // Get scenes from current board
  const scenes = useMemo(() => {
    if (!activeBoard || !boards[activeBoard]) return [];

    const board = boards[activeBoard];
    const allCards = board.columns.flatMap((col) => col.cards);

    // Convert cards to scenes format - use card content as scene text
    return allCards.map((card, index) => ({
      id: card.id,
      title: card.title,
      text: card.description || card.notes || card.title, // Use available text content
      order: card.order ?? index,
    }));
  }, [activeBoard, boards]);

  const run = useCallback(async () => {
    if (!featureFlags.isEnabled('aiPlotAnalysis') || scenes.length === 0) {
      console.warn('Plot analysis disabled or no scenes available');
      return;
    }

    try {
      const input = {
        profileId,
        projectId,
        scenes,
        structure: 'three_act' as const, // Default structure
      };

      devLog.debug('Running plot analysis for', scenes.length, 'scenes');
      const service: any = (aiService as any) || {};
      const result = service.analyzeBoard
        ? await service.analyzeBoard(input)
        : await service.analyzeProject(projectId, { mockMode: true });
      setAnalysis(projectId, result);

      // Track analytics
      analyticsService.track('plot_analysis_run', {
        projectId,
        scenes: scenes.length,
        model: result.model,
        qualityScore: result.qualityScore,
        issuesFound: result.issues.length,
      });

      devLog.debug('Plot analysis complete:', result);
    } catch (error) {
      console.error('Plot analysis failed:', error);

      // Track failure
      analyticsService.track('plot_analysis_failed', {
        projectId,
        scenes: scenes.length,
        error: error instanceof Error ? error.message : 'Unknown error',
      });
    }
  }, [profileId, projectId, scenes, setAnalysis]);

  return {
    analysis,
    run,
    hasScenes: scenes.length > 0,
    sceneCount: scenes.length,
    isEnabled: featureFlags.isEnabled('aiPlotAnalysis'),
  };
}

// src/hooks/useAdvancedFocusMode.ts
// Enhanced focus mode with typewriter mode, writing sprints, and ambient sounds

import { useState, useCallback, useEffect, useRef } from 'react';

export interface FocusSettings {
  typewriterMode: boolean;
  showWordCount: boolean;
  showTimer: boolean;
  ambientSound: string;
  ambientVolume: number;
  hideUI: boolean;
  zenMode: boolean; // Ultra-minimal UI
}

export interface SprintSettings {
  duration: number; // in minutes
  wordTarget: number;
  breakDuration: number; // in minutes
  autoStartBreaks: boolean;
}

export interface SprintState {
  isActive: boolean;
  isPaused: boolean;
  isOnBreak: boolean;
  startTime: number;
  pausedTime: number;
  duration: number; // in seconds
  remainingTime: number;
  target: number;
  wordsAtStart: number;
  currentWords: number;
  sprintsCompleted: number;
}

export interface SessionStats {
  sessionStartTime: number;
  totalWordsWritten: number;
  totalSprintTime: number; // in seconds
  sprintsCompleted: number;
  averageWPM: number;
}

const AMBIENT_SOUNDS = [
  { id: 'none', name: 'None', url: '' },
  { id: 'rain', name: 'Gentle Rain', url: '/sounds/rain.mp3' },
  { id: 'forest', name: 'Forest Ambience', url: '/sounds/forest.mp3' },
  { id: 'cafe', name: 'Coffee Shop', url: '/sounds/cafe.mp3' },
  { id: 'ocean', name: 'Ocean Waves', url: '/sounds/ocean.mp3' },
  { id: 'fireplace', name: 'Fireplace', url: '/sounds/fireplace.mp3' },
  { id: 'white-noise', name: 'White Noise', url: '/sounds/white-noise.mp3' },
  { id: 'library', name: 'Library Ambience', url: '/sounds/library.mp3' },
];

export function useAdvancedFocusMode() {
  // Basic focus mode state
  const [isFocusMode, setIsFocusMode] = useState(false);

  // Advanced focus settings
  const [settings, setSettings] = useState<FocusSettings>({
    typewriterMode: false,
    showWordCount: true,
    showTimer: true,
    ambientSound: 'none',
    ambientVolume: 0.3,
    hideUI: false,
    zenMode: false,
  });

  // Sprint functionality
  const [sprintSettings, setSprintSettings] = useState<SprintSettings>({
    duration: 25, // Pomodoro default
    wordTarget: 500,
    breakDuration: 5,
    autoStartBreaks: true,
  });

  const [sprint, setSprint] = useState<SprintState>({
    isActive: false,
    isPaused: false,
    isOnBreak: false,
    startTime: 0,
    pausedTime: 0,
    duration: 0,
    remainingTime: 0,
    target: 0,
    wordsAtStart: 0,
    currentWords: 0,
    sprintsCompleted: 0,
  });

  // Session statistics
  const [sessionStats, setSessionStats] = useState<SessionStats>({
    sessionStartTime: Date.now(),
    totalWordsWritten: 0,
    totalSprintTime: 0,
    sprintsCompleted: 0,
    averageWPM: 0,
  });

  // Audio management
  const [isMuted, setIsMuted] = useState(false);
  const audioRef = useRef<HTMLAudioElement | null>(null);

  // Replace NodeJS.Timeout with a cross-env type
  type _TimeoutRef = ReturnType<typeof setInterval>;
  const sprintTimerRef = useRef<number | null>(null);

  // Load settings from localStorage
  useEffect(() => {
    const savedSettings = localStorage.getItem('inkwell_focus_settings');
    if (savedSettings) {
      try {
        const parsed = JSON.parse(savedSettings);
        setSettings((prev) => ({ ...prev, ...parsed }));
      } catch (e) {
        console.warn('Failed to load focus settings:', e);
      }
    }

    const savedSprintSettings = localStorage.getItem('inkwell_sprint_settings');
    if (savedSprintSettings) {
      try {
        const parsed = JSON.parse(savedSprintSettings);
        setSprintSettings((prev) => ({ ...prev, ...parsed }));
      } catch (e) {
        console.warn('Failed to load sprint settings:', e);
      }
    }
  }, []);

  // Save settings to localStorage
  useEffect(() => {
    localStorage.setItem('inkwell_focus_settings', JSON.stringify(settings));
  }, [settings]);

  useEffect(() => {
    localStorage.setItem('inkwell_sprint_settings', JSON.stringify(sprintSettings));
  }, [sprintSettings]);

  // Ambient sound management
  useEffect(() => {
    if (settings.ambientSound && settings.ambientSound !== 'none') {
      const sound = AMBIENT_SOUNDS.find((s) => s.id === settings.ambientSound);
      if (sound) {
        if (audioRef.current) {
          audioRef.current.pause();
        }

        audioRef.current = new Audio(sound.url);
        audioRef.current.loop = true;
        audioRef.current.volume = isMuted ? 0 : settings.ambientVolume;

        if (isFocusMode) {
          audioRef.current.play().catch((e) => console.warn('Failed to play ambient sound:', e));
        }
      }
    } else {
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current = null;
      }
    }

    return () => {
      if (audioRef.current) {
        audioRef.current.pause();
      }
    };
  }, [settings.ambientSound, settings.ambientVolume, isMuted, isFocusMode]);

  // Sprint timer management
  useEffect(() => {
    if (sprint.isActive && !sprint.isPaused && !sprint.isOnBreak) {
      sprintTimerRef.current = window.setInterval(() => {
        // Use window.setInterval
        setSprint((prev) => {
          const elapsed = Date.now() - prev.startTime - prev.pausedTime;
          const remaining = Math.max(0, prev.duration * 1000 - elapsed);

          if (remaining === 0) {
            return {
              ...prev,
              isActive: false,
              isOnBreak: sprintSettings.autoStartBreaks,
              remainingTime: 0,
              sprintsCompleted: prev.sprintsCompleted + 1,
            };
          }

          return {
            ...prev,
            remainingTime: Math.floor(remaining / 1000),
          };
        });
      }, 1000);
    } else {
      if (sprintTimerRef.current) {
        window.clearInterval(sprintTimerRef.current); // Use window.clearInterval
        sprintTimerRef.current = null;
      }
    }

    return () => {
      if (sprintTimerRef.current) {
        window.clearInterval(sprintTimerRef.current);
      }
    };
  }, [sprint.isActive, sprint.isPaused, sprint.isOnBreak, sprintSettings.autoStartBreaks]);

  // Focus mode management
  const toggleFocusMode = useCallback(() => {
    setIsFocusMode((prev) => !prev);
  }, []);

  const enableFocusMode = useCallback(() => {
    setIsFocusMode(true);
  }, []);

  const disableFocusMode = useCallback(() => {
    setIsFocusMode(false);
    // Stop any active sprints when exiting focus mode
    if (sprint.isActive) {
      stopSprint();
    }
  }, []);

  // Sprint management
  const startSprint = useCallback(
    (wordCount: number = 0) => {
      const duration = sprintSettings.duration * 60; // Convert to seconds
      setSprint({
        isActive: true,
        isPaused: false,
        isOnBreak: false,
        startTime: Date.now(),
        pausedTime: 0,
        duration,
        remainingTime: duration,
        target: sprintSettings.wordTarget,
        wordsAtStart: wordCount,
        currentWords: wordCount,
        sprintsCompleted: 0,
      });

      // Update session stats
      setSessionStats((prev) => ({
        ...prev,
        sessionStartTime: prev.sessionStartTime || Date.now(),
      }));
    },
    [sprintSettings],
  );

  const pauseSprint = useCallback(() => {
    setSprint((prev) => ({
      ...prev,
      isPaused: true,
    }));
  }, []);

  const resumeSprint = useCallback(() => {
    setSprint((prev) => ({
      ...prev,
      isPaused: false,
      pausedTime: prev.pausedTime + (Date.now() - prev.startTime),
    }));
  }, []);

  const stopSprint = useCallback(() => {
    setSprint((prev) => ({
      ...prev,
      isActive: false,
      isPaused: false,
      isOnBreak: false,
      remainingTime: 0,
    }));
  }, []);

  // Update word count during sprint
  const updateSprintWordCount = useCallback(
    (wordCount: number) => {
      setSprint((prev) => ({
        ...prev,
        currentWords: wordCount,
      }));

      // Update session stats
      setSessionStats((prev) => ({
        ...prev,
        totalWordsWritten: Math.max(
          prev.totalWordsWritten,
          wordCount - sessionStats.sessionStartTime,
        ),
      }));
    },
    [sessionStats.sessionStartTime],
  );

  // Settings management
  const updateSettings = useCallback((newSettings: Partial<FocusSettings>) => {
    setSettings((prev) => ({ ...prev, ...newSettings }));
  }, []);

  const updateSprintSettings = useCallback((newSettings: Partial<SprintSettings>) => {
    setSprintSettings((prev) => ({ ...prev, ...newSettings }));
  }, []);

  // Audio controls
  const toggleMute = useCallback(() => {
    setIsMuted((prev) => !prev);
    if (audioRef.current) {
      audioRef.current.volume = isMuted ? settings.ambientVolume : 0;
    }
  }, [isMuted, settings.ambientVolume]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      // Toggle focus mode with F11 or Cmd/Ctrl + Shift + F
      if (
        event.key === 'F11' ||
        ((event.metaKey || event.ctrlKey) && event.shiftKey && event.key === 'F')
      ) {
        event.preventDefault();
        toggleFocusMode();
      }

      // Exit focus mode with Escape
      if (event.key === 'Escape' && isFocusMode) {
        event.preventDefault();
        disableFocusMode();
      }

      // Sprint controls (only in focus mode)
      if (isFocusMode) {
        // Start sprint with Cmd/Ctrl + Shift + S
        if ((event.metaKey || event.ctrlKey) && event.shiftKey && event.key === 'S') {
          event.preventDefault();
          if (!sprint.isActive) {
            startSprint();
          } else if (sprint.isPaused) {
            resumeSprint();
          } else {
            pauseSprint();
          }
        }

        // Stop sprint with Cmd/Ctrl + Shift + X
        if ((event.metaKey || event.ctrlKey) && event.shiftKey && event.key === 'X') {
          event.preventDefault();
          stopSprint();
        }

        // Toggle mute with Cmd/Ctrl + Shift + M
        if ((event.metaKey || event.ctrlKey) && event.shiftKey && event.key === 'M') {
          event.preventDefault();
          toggleMute();
        }
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [
    isFocusMode,
    sprint,
    toggleFocusMode,
    disableFocusMode,
    startSprint,
    pauseSprint,
    resumeSprint,
    stopSprint,
    toggleMute,
  ]);

  // Apply focus mode styles
  useEffect(() => {
    if (isFocusMode) {
      document.body.classList.add('focus-mode');
      if (settings.zenMode) {
        document.body.classList.add('zen-mode');
      }
      if (settings.typewriterMode) {
        document.body.classList.add('typewriter-mode');
      }
      document.body.style.overflow = 'hidden';

      const originalTitle = document.title;
      document.title = `${originalTitle} - Focus Mode`;

      return () => {
        document.body.classList.remove('focus-mode', 'zen-mode', 'typewriter-mode');
        document.body.style.overflow = '';
        document.title = originalTitle;
      };
    } else {
      document.body.classList.remove('focus-mode', 'zen-mode', 'typewriter-mode');
      document.body.style.overflow = '';
    }
    return () => {}; // Always return cleanup function
  }, [isFocusMode, settings.zenMode, settings.typewriterMode]);

  // Calculate sprint progress
  const sprintProgress =
    sprint.isActive && sprint.duration > 0
      ? ((sprint.duration - sprint.remainingTime) / sprint.duration) * 100
      : 0;

  const wordsProgress =
    sprint.target > 0
      ? Math.min(((sprint.currentWords - sprint.wordsAtStart) / sprint.target) * 100, 100)
      : 0;

  return {
    // Basic focus mode
    isFocusMode,
    toggleFocusMode,
    enableFocusMode,
    disableFocusMode,

    // Settings
    settings,
    updateSettings,
    sprintSettings,
    updateSprintSettings,

    // Sprint functionality
    sprint,
    startSprint,
    pauseSprint,
    resumeSprint,
    stopSprint,
    updateSprintWordCount,
    sprintProgress,
    wordsProgress,

    // Session stats
    sessionStats,

    // Audio
    isMuted,
    toggleMute,
    ambientSounds: AMBIENT_SOUNDS,

    // Utilities
    formatTime: (seconds: number) => {
      const mins = Math.floor(seconds / 60);
      const secs = seconds % 60;
      return `${mins}:${secs.toString().padStart(2, '0')}`;
    },
  };
}

/**
 * Auto-start Spotlight Tour Hook
 * Manages first-run detection and tour auto-start
 */
import devLog from "src/utils/devLogger";

import { useEffect, useState } from 'react';

import { getSpotlightSteps } from '@/tour/getSpotlightSteps';
import { tourService } from '@/tour/TourService';

const TOUR_COMPLETED_KEY = 'inkwell:tour:completed';
const TOUR_ID = 'onboarding-v2';

/**
 * Check if the user has completed the tour before
 */
function hasCompletedTour(): boolean {
  try {
    const completed = localStorage.getItem(TOUR_COMPLETED_KEY);
    return completed === 'true';
  } catch {
    return false;
  }
}

/**
 * Mark the tour as completed
 */
function markTourCompleted(): void {
  try {
    localStorage.setItem(TOUR_COMPLETED_KEY, 'true');
  } catch (error) {
    console.warn('[useAutostartSpotlight] Failed to mark tour as completed:', error);
  }
}

/**
 * Hook to auto-start the Spotlight Tour for first-time users
 */
export function useAutostartSpotlight() {
  const [shouldAutoStart, setShouldAutoStart] = useState(false);

  useEffect(() => {
    // Check if we should auto-start the tour
    const isFirstRun = !hasCompletedTour();
    const isTourRunning = tourService.isRunning();

    if (isFirstRun && !isTourRunning) {
      setShouldAutoStart(true);
    }
  }, []);

  useEffect(() => {
    if (!shouldAutoStart) return;

    // Small delay to ensure DOM is ready and components are mounted
    const timer = setTimeout(() => {
      const steps = getSpotlightSteps();

      tourService.start({
        id: TOUR_ID,
        steps,
        showProgress: true,
        allowSkip: true,
        onComplete: () => {
          markTourCompleted();
          devLog.debug('[useAutostartSpotlight] Tour completed');
        },
        onSkip: () => {
          markTourCompleted();
          devLog.debug('[useAutostartSpotlight] Tour skipped');
        },
      });

      setShouldAutoStart(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, [shouldAutoStart]);

  /**
   * Manually restart the tour (e.g., from Settings)
   */
  const restartTour = () => {
    const steps = getSpotlightSteps();

    tourService.start({
      id: TOUR_ID,
      steps,
      showProgress: true,
      allowSkip: true,
      onComplete: () => {
        devLog.debug('[useAutostartSpotlight] Manual tour completed');
      },
      onSkip: () => {
        devLog.debug('[useAutostartSpotlight] Manual tour skipped');
      },
    });
  };

  return {
    restartTour,
  };
}

