// Plot Analysis Panel - Main component with tabs
import devLog from "src/utils/devLogger";

import { Loader2, AlertTriangle } from 'lucide-react';
import { useState, useMemo, useEffect } from 'react';

import type { Project } from '@/context/AppContext';
import { storeCapturedCharts, batchCaptureSVGs } from '@/export/utils/svgCapture';

import { usePlotAnalysis } from '../hooks/usePlotAnalysis';

import { ArcHeatmap } from './ArcHeatmap';
import { InsightList } from './InsightList';
import { PacingChart } from './PacingChart';
import { ScorecardSummary } from './ScorecardSummary';

import type { SeverityFilter } from '../types.ui';

type Tab = 'overview' | 'pacing' | 'arcs';

interface PlotAnalysisPanelProps {
  project: Project;
  onOpenChapter?: (chapterIndex: number) => void;
}

export function PlotAnalysisPanel({ project, onOpenChapter }: PlotAnalysisPanelProps) {
  const { status, result, error, lastAnalyzedAt, run } = usePlotAnalysis({ project });
  const [activeTab, setActiveTab] = useState<Tab>('overview');
  const [severityFilter, setSeverityFilter] = useState<SeverityFilter>('all');

  // Extract highlighted chapters from high-severity insights
  const highlightedChapters = useMemo(() => {
    if (!result) return [];
    return result.insights.filter((i) => i.severity === 'high').flatMap((i) => i.affectedChapters);
  }, [result]);

  // Capture SVG charts after they render for PDF export
  useEffect(() => {
    if (!result || status !== 'ready') return;

    // Wait for charts to fully render
    const timer = setTimeout(() => {
      try {
        // Define selectors for plot analysis charts
        // These match the Recharts ResponsiveContainer SVG outputs
        const charts = batchCaptureSVGs([
          'div[aria-label="Pacing by chapter"] svg', // PacingChart
          'div[aria-label="Arc presence heatmap"] svg', // ArcHeatmap
        ]);

        // Store captured charts in localStorage for export
        storeCapturedCharts(project.id, {
          pacing: charts['div[aria-label="Pacing by chapter"] svg'] || null,
          arcs: charts['div[aria-label="Arc presence heatmap"] svg'] || null,
        });

        devLog.debug('Plot analysis charts captured for export');
      } catch (error) {
        console.warn('Failed to capture plot analysis charts:', error);
      }
    }, 500); // Allow time for animations to complete

    return () => clearTimeout(timer);
  }, [result, status, project.id]);

  // Loading state
  if (status === 'loading') {
    return (
      <div className="flex flex-col items-center justify-center py-16 space-y-4">
        <Loader2 className="w-12 h-12 animate-spin text-ink-500" />
        <p className="text-text-2">Analyzing plot structure...</p>
        <p className="text-sm text-text-2">This may take up to 10 seconds</p>
      </div>
    );
  }

  // Error state
  if (status === 'error') {
    return (
      <div className="flex flex-col items-center justify-center py-16 space-y-4">
        <AlertTriangle className="w-12 h-12 text-red-600" />
        <p className="text-text-1 font-semibold">Analysis Failed</p>
        <p className="text-sm text-text-2 max-w-md text-center">{error}</p>
        {result && <p className="text-xs text-text-2">Showing last successful analysis</p>}
        <button
          onClick={run}
          className="px-4 py-2 rounded-lg bg-ink-500 text-white hover:bg-ink-600 transition-colors"
        >
          Retry Analysis
        </button>
      </div>
    );
  }

  // Empty state
  if (!result) {
    const chapterCount = project.chapters?.length ?? 0;
    const totalWords =
      project.chapters?.reduce(
        (sum: number, ch: any) =>
          sum + (ch.content?.split(/\s+/).filter((w: string) => w.length > 0).length ?? 0),
        0,
      ) ?? 0;

    const canAnalyze = chapterCount >= 3 && totalWords >= 3000;

    return (
      <div className="flex flex-col items-center justify-center py-16 space-y-4">
        <div className="text-center space-y-2">
          <h3 className="text-xl font-semibold text-text-1">Plot Analysis</h3>
          <p className="text-text-2">
            {chapterCount < 3
              ? `Add at least ${3 - chapterCount} more chapter${3 - chapterCount > 1 ? 's' : ''} to analyze your plot.`
              : totalWords < 3000
                ? `Add at least ${Math.ceil((3000 - totalWords) / 500)} more chapters (~${3000 - totalWords} words) to run analysis.`
                : 'Ready to analyze your plot structure!'}
          </p>
          <div className="text-sm text-text-2 space-y-1 mt-4">
            <p>
              Current: {chapterCount} chapters, ~{totalWords.toLocaleString()} words
            </p>
            <p>Required: 3+ chapters, 3,000+ words</p>
          </div>
        </div>
        {canAnalyze && (
          <button
            onClick={run}
            className="mt-6 px-6 py-3 rounded-lg bg-ink-500 text-white hover:bg-ink-600 transition-colors font-medium"
          >
            Run Analysis
          </button>
        )}
      </div>
    );
  }

  // Tabs
  const tabs: Array<{ id: Tab; label: string }> = [
    { id: 'overview', label: 'Overview' },
    { id: 'pacing', label: 'Pacing' },
    { id: 'arcs', label: 'Arcs' },
  ];

  return (
    <div className="space-y-6">
      {/* Scorecard */}
      <ScorecardSummary
        scorecard={result.scorecard}
        lastAnalyzedAt={lastAnalyzedAt}
        onRerun={run}
      />

      {/* Tabs */}
      <div className="border-b border-subtle">
        <nav className="flex space-x-8" role="tablist">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              role="tab"
              aria-selected={activeTab === tab.id}
              aria-controls={`${tab.id}-panel`}
              onClick={() => setActiveTab(tab.id)}
              className={`py-3 px-1 border-b-2 font-medium text-sm transition-colors ${
                activeTab === tab.id
                  ? 'border-ink-500 text-ink-700'
                  : 'border-transparent text-text-2 hover:text-text-1 hover:border-gray-300'
              }`}
            >
              {tab.label}
            </button>
          ))}
        </nav>
      </div>

      {/* Tab panels */}
      <div role="tabpanel" id={`${activeTab}-panel`} aria-labelledby={`${activeTab}-tab`}>
        {activeTab === 'overview' && (
          <div className="space-y-6">
            <InsightList
              insights={result.insights}
              filter={severityFilter}
              onFilterChange={setSeverityFilter}
              onOpenChapter={onOpenChapter ?? (() => {})}
            />
            {result.notes && (
              <div className="rounded-lg border border-subtle bg-surface-1 p-4">
                <p className="text-sm text-text-2">{result.notes}</p>
              </div>
            )}
          </div>
        )}

        {activeTab === 'pacing' && (
          <div className="space-y-4">
            <PacingChart
              chapters={result.chapters}
              highlightedChapters={highlightedChapters}
              showRollingAvg={true}
            />
          </div>
        )}

        {activeTab === 'arcs' && (
          <div className="space-y-4">
            <ArcHeatmap chapters={result.chapters} />
          </div>
        )}
      </div>
    </div>
  );
}

// Data preparation for Plot Analysis

import type { Project } from '@/context/AppContext';

import type { BookMetrics, ChapterMetrics, PlotAnalysisFilters } from './types';

const MAX_SUMMARY_CHARS = 1200;
const MAX_SUMMARY_TOKENS = 220; // Rough estimate: ~5.5 chars per token
const MAX_CHAPTERS = 30;
const CONFLICT_VERBS = [
  'struggle',
  'lose',
  'risk',
  'discover',
  'chase',
  'fight',
  'escape',
  'confront',
];

/**
 * Truncate text to max chars on sentence boundary
 */
function truncateOnSentence(text: string, maxChars: number): string {
  if (text.length <= maxChars) return text;

  const truncated = text.slice(0, maxChars);
  const lastPeriod = truncated.lastIndexOf('.');
  const lastQuestion = truncated.lastIndexOf('?');
  const lastExclaim = truncated.lastIndexOf('!');

  const lastSentence = Math.max(lastPeriod, lastQuestion, lastExclaim);

  if (lastSentence > maxChars * 0.7) {
    return truncated.slice(0, lastSentence + 1);
  }

  return truncated + '...';
}

/**
 * Redact PII (emails, URLs) from text
 */
function redactPII(text: string): string {
  // Redact emails
  let cleaned = text.replace(/\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/g, '[REDACTED]');

  // Redact URLs
  cleaned = cleaned.replace(/https?:\/\/[^\s]+/g, '[REDACTED]');

  return cleaned;
}

/**
 * Extract first N words from chapter content as summary
 */
function extractSummary(content: string, maxWords: number = 200): string {
  if (!content) return '';

  const words = content.trim().split(/\s+/);
  const summary = words.slice(0, maxWords).join(' ');

  return truncateOnSentence(summary, MAX_SUMMARY_CHARS);
}

/**
 * Compute word count for text
 */
export function countWords(text: string): number {
  if (!text) return 0;
  return text
    .trim()
    .split(/\s+/)
    .filter((word) => word.length > 0).length;
}

/**
 * Extract tags from chapter (POV, arc markers, etc.)
 */
function extractTags(chapter: any): string[] {
  const tags: string[] = [];

  // Add POV if present
  if (chapter.pov) {
    tags.push(`pov:${chapter.pov}`);
  }

  // Add status tags
  if (chapter.status) {
    tags.push(chapter.status.toLowerCase());
  }

  // Add arc markers if present
  if (chapter.arcMarker) {
    tags.push(chapter.arcMarker.toLowerCase());
  }

  // Check content for structural markers
  const content = (chapter.content || '').toLowerCase();
  if (content.includes('setup') || content.includes('introduction')) tags.push('setup');
  if (CONFLICT_VERBS.some((verb) => content.includes(verb))) tags.push('conflict');
  if (content.includes('turn') || content.includes('twist')) tags.push('turn');
  if (content.includes('resolution') || content.includes('payoff')) tags.push('payoff');
  if (content.includes('aftermath') || content.includes('epilogue')) tags.push('aftermath');

  return [...new Set(tags)]; // Deduplicate
}

/**
 * Sample chapters evenly if count exceeds limit
 */
function sampleChapters(chapters: any[], maxCount: number): { chapters: any[]; sampled: boolean } {
  if (chapters.length <= maxCount) {
    return { chapters, sampled: false };
  }

  const step = chapters.length / maxCount;
  const sampled = [];

  for (let i = 0; i < maxCount; i++) {
    const index = Math.floor(i * step);
    sampled.push(chapters[index]);
  }

  return { chapters: sampled, sampled: true };
}

/**
 * Prepare chapter metrics from project data
 */
export function prepareChapterMetrics(
  project: Project,
  filters: PlotAnalysisFilters,
): ChapterMetrics[] {
  if (!project.chapters || project.chapters.length === 0) {
    return [];
  }

  let chapters = project.chapters;

  // Apply filters
  if (!filters.includeNotes) {
    chapters = chapters.filter((ch: any) => ch.status !== 'notes');
  }

  if (!filters.includeDrafts) {
    chapters = chapters.filter((ch: any) => ch.status !== 'draft');
  }

  if (filters.excludedChapterIds.length > 0) {
    chapters = chapters.filter((ch: any) => !filters.excludedChapterIds.includes(ch.id));
  }

  // Convert to metrics
  const metrics: ChapterMetrics[] = chapters.map((ch: any, index: number) => {
    const content = ch.content || '';
    const summary = extractSummary(content);
    const cleanSummary = redactPII(summary);

    return {
      i: index,
      title: ch.title || `Chapter ${index + 1}`,
      words: countWords(content),
      pov: ch.pov,
      tags: extractTags(ch),
      updatedAt: ch.updatedAt ? new Date(ch.updatedAt).toISOString() : new Date().toISOString(),
      summary: cleanSummary,
    };
  });

  return metrics;
}

/**
 * Prepare book-level metrics for analysis
 */
export function prepareBookMetrics(
  project: Project,
  filters: PlotAnalysisFilters,
): { data: BookMetrics; sampled: boolean } {
  const allMetrics = prepareChapterMetrics(project, filters);
  const { chapters, sampled } = sampleChapters(allMetrics, MAX_CHAPTERS);

  const totalWords = chapters.reduce((sum, ch) => sum + ch.words, 0);

  return {
    data: {
      chapters,
      totalWords,
      targets: {
        midpointPct: [0.45, 0.55],
        incitingPctMax: 0.2,
        climaxPct: [0.8, 0.95],
      },
    },
    sampled,
  };
}

/**
 * Compute project hash for cache invalidation
 */
export function computeProjectHash(project: Project): string {
  const content = project.chapters?.map((ch: any) => `${ch.id}:${ch.updatedAt}`).join('|') || '';

  // Simple hash (not cryptographic, just for cache busting)
  let hash = 0;
  for (let i = 0; i < content.length; i++) {
    const char = content.charCodeAt(i);
    hash = (hash << 5) - hash + char;
    hash = hash & hash; // Convert to 32-bit integer
  }

  return Math.abs(hash).toString(36);
}

// React hook for managing plot analysis state and execution
import devLog from "src/utils/devLogger";

import { useCallback, useEffect, useMemo, useRef, useState } from 'react';

import type { Project } from '@/context/AppContext';

import { computeProjectHash } from '../dataPrep';
import { analyzePlot } from '../plotAnalysisService';

import type { AnalysisResult } from '../types';
import type { PlotFilters } from '../types.ui';

type Status = 'idle' | 'loading' | 'ready' | 'error';

const DEFAULT_FILTERS: PlotFilters = {
  includeDrafts: false,
  includeNotes: false,
  minWords: 0,
  pov: 'all',
};

interface UsePlotAnalysisOptions {
  project: Project;
  initialFilters?: Partial<PlotFilters>;
}

export function usePlotAnalysis({ project, initialFilters }: UsePlotAnalysisOptions) {
  const [status, setStatus] = useState<Status>('idle');
  const [error, setError] = useState<string | null>(null);
  const [result, setResult] = useState<AnalysisResult | null>(null);
  const [lastAnalyzedAt, setLastAnalyzedAt] = useState<number | null>(null);

  const [filters, setFilters] = useState<PlotFilters>({
    ...DEFAULT_FILTERS,
    ...initialFilters,
  });

  const abortRef = useRef<AbortController | null>(null);

  // Local cache key for UI state persistence
  const projectHash = useMemo(() => computeProjectHash(project), [project]);
  const cacheKey = useMemo(() => `plot:ui:${projectHash}`, [projectHash]);

  // Rehydrate from localStorage on mount
  useEffect(() => {
    try {
      const raw = localStorage.getItem(cacheKey);
      if (!raw) return;

      const parsed = JSON.parse(raw) as {
        result: AnalysisResult;
        lastAnalyzedAt: number;
        filters?: PlotFilters;
      };

      // Only use cached result if project hash matches
      if (parsed.result.projectHash === projectHash) {
        setResult(parsed.result);
        setLastAnalyzedAt(parsed.lastAnalyzedAt ?? null);
        if (parsed.filters) setFilters((f) => ({ ...f, ...parsed.filters }));
        setStatus('ready');
      }
    } catch {
      // Ignore parse errors
    }
  }, [cacheKey, projectHash]);

  // Persist UI cache when result changes
  useEffect(() => {
    if (!result) return;

    try {
      localStorage.setItem(cacheKey, JSON.stringify({ result, lastAnalyzedAt, filters }));
    } catch {
      // Storage full or blocked
    }
  }, [cacheKey, result, lastAnalyzedAt, filters]);

  const invalidate = useCallback(() => {
    setResult(null);
    setLastAnalyzedAt(null);
    setStatus('idle');
    try {
      localStorage.removeItem(cacheKey);
    } catch {
      // Ignore errors
    }
  }, [cacheKey]);

  const run = useCallback(async () => {
    // Cancel prior run
    if (abortRef.current) abortRef.current.abort();
    const ctl = new AbortController();
    abortRef.current = ctl;

    setStatus('loading');
    setError(null);

    const started = performance.now();

    try {
      // Convert UI filters to service filters
      const serviceFilters = {
        includeNotes: filters.includeNotes,
        includeDrafts: filters.includeDrafts,
        excludedChapterIds: [],
      };

      const next = await analyzePlot(project, serviceFilters, {
        useLLM: true,
        forceRefresh: false,
      });

      if (ctl.signal.aborted) return;

      setResult(next);
      setLastAnalyzedAt(Date.now());
      setStatus('ready');

      devLog.debug(`Analysis completed in ${Math.round(performance.now() - started)}ms`);
    } catch (e: any) {
      if (ctl.signal.aborted) return;

      setStatus('error');
      setError(e?.message ?? 'Analysis failed');
      console.error('Plot analysis failed:', e);
    }
  }, [project, filters]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (abortRef.current) abortRef.current.abort();
    };
  }, []);

  return {
    status,
    error,
    result,
    lastAnalyzedAt,
    filters,
    setFilters,
    run,
    invalidate,
  };
}

// @ts-nocheck
// src/services/searchService.ts
import devLog from "src/utils/devLogger";
import type { Scene } from '@/types/writing';

import { storageService } from './storageService';

export interface SearchResult {
  id: string;
  type: 'scene' | 'chapter' | 'character' | 'plot';
  title: string;
  content: string;
  excerpt: string;
  score: number;
  projectId: string;
  chapterId?: string;
  metadata?: {
    wordCount?: number;
    status?: string;
    lastModified?: Date;
  };
}

export interface SearchOptions {
  types?: Array<'scene' | 'chapter' | 'character' | 'plot'>;
  maxResults?: number;
  minScore?: number;
  projectId?: string;
}

export interface SearchStats {
  totalDocuments: number;
  indexSize: number;
  lastUpdate: number;
  queryCount: number;
  averageLatency: number;
}

interface IndexEntry {
  term: string;
  documents: Map<
    string,
    {
      frequency: number;
      positions: number[];
    }
  >;
}

class SearchService {
  private indexes = new Map<string, Map<string, IndexEntry>>();
  private documents = new Map<string, Map<string, SearchResult>>();
  private stats = new Map<string, SearchStats>();
  private queryHistory: string[] = [];
  private performanceMetrics: number[] = [];

  // Initialize search service for a project
  async initializeProject(projectId: string): Promise<void> {
    if (this.indexes.has(projectId)) {
      return; // Already initialized
    }

    devLog.debug(`Initializing search index for project ${projectId}`);

    const startTime = Date.now();
    const index = new Map<string, IndexEntry>();
    const documents = new Map<string, SearchResult>();

    try {
      // Load project data
      const project = storageService.loadProject(projectId);
      const chapters = await storageService.loadWritingChapters(projectId);

      if (!project) {
        throw new Error(`Project ${projectId} not found`);
      }

      let totalDocuments = 0;

      // Index chapters
      chapters.forEach((chapter) => {
        const result: SearchResult = {
          id: chapter.id,
          type: 'chapter',
          title: chapter.title,
          content: chapter.scenes.map((s: Scene) => s.content).join('\n\n'),
          excerpt: this.createExcerpt(chapter.title, 150),
          score: 0,
          projectId,
          metadata: {
            wordCount: chapter.totalWordCount,
            status: chapter.status,
            lastModified: chapter.updatedAt,
          },
        };

        documents.set(chapter.id, result);
        this.indexDocument(index, chapter.id, chapter.title + '\n' + result.content);
        totalDocuments++;

        // Index individual scenes
        chapter.scenes.forEach((scene) => {
          const sceneResult: SearchResult = {
            id: scene.id,
            type: 'scene',
            title: scene.title,
            content: scene.content,
            excerpt: this.createExcerpt(scene.content, 150),
            score: 0,
            projectId,
            chapterId: chapter.id,
            metadata: {
              wordCount: scene.wordCount,
              status: scene.status,
              lastModified: scene.updatedAt,
            },
          };

          documents.set(scene.id, sceneResult);
          this.indexDocument(index, scene.id, scene.title + '\n' + scene.content);
          totalDocuments++;
        });
      });

      // Index characters if available
      if ((project as any).characters) {
        (project as any).characters.forEach((character: any) => {
          const result: SearchResult = {
            id: character.id,
            type: 'character',
            title: character.name,
            content: `${character.description}\n${character.backstory}\n${character.personality?.join(' ') || ''}`,
            excerpt: this.createExcerpt(character.description, 150),
            score: 0,
            projectId,
          };

          documents.set(character.id, result);
          this.indexDocument(index, character.id, result.title + '\n' + result.content);
          totalDocuments++;
        });
      }

      // Store indexes
      this.indexes.set(projectId, index);
      this.documents.set(projectId, documents);

      // Update stats
      const indexTime = Date.now() - startTime;
      this.stats.set(projectId, {
        totalDocuments,
        indexSize: this.calculateIndexSize(index),
        lastUpdate: Date.now(),
        queryCount: 0,
        averageLatency: 0,
      });

      devLog.debug(
        `Search index built for project ${projectId}: ${totalDocuments} documents in ${indexTime}ms`,
      );
    } catch (error) {
      console.error(`Failed to initialize search for project ${projectId}:`, error);
      throw error;
    }
  }

  // Search across project content
  async search(query: string, options: SearchOptions = {}): Promise<SearchResult[]> {
    const startTime = Date.now();
    const {
      types = ['scene', 'chapter', 'character', 'plot'],
      maxResults = 20,
      minScore = 0.001,
      projectId,
    } = options;

    if (!projectId) {
      throw new Error('Project ID is required for search');
    }

    try {
      // Ensure project is indexed
      await this.initializeProject(projectId);

      const index = this.indexes.get(projectId);
      const documents = this.documents.get(projectId);

      if (!index || !documents) {
        return [];
      }

      // Parse and normalize query
      const terms = this.tokenize(query.toLowerCase());
      if (terms.length === 0) {
        return [];
      }

      // Calculate scores using BM25
      const scores = new Map<string, number>();
      const docCount = documents.size;

      terms.forEach((term) => {
        const entry = index.get(term);
        if (!entry) return;

        const idf = Math.max(
          0.001,
          Math.log((docCount - entry.documents.size + 0.5) / (entry.documents.size + 0.5)),
        );

        entry.documents.forEach((termDoc, docId) => {
          if (!documents.has(docId)) return;

          const doc = documents.get(docId)!;

          // Filter by type
          if (!types.includes(doc.type)) return;

          // BM25 parameters
          const k1 = 1.5;
          const b = 0.75;
          const avgdl = this.getAverageDocumentLength(documents);
          const docLength = doc.content.length;

          const tf = termDoc.frequency;
          const score = (idf * (tf * (k1 + 1))) / (tf + k1 * (1 - b + b * (docLength / avgdl)));

          scores.set(docId, (scores.get(docId) || 0) + score);
        });
      });

      // Prepare results
      const results: SearchResult[] = Array.from(scores.entries())
        .filter(([, score]) => score >= minScore)
        .sort(([, scoreA], [, scoreB]) => scoreB - scoreA)
        .slice(0, maxResults)
        .map(([docId, score]) => {
          const doc = documents.get(docId)!;
          return {
            ...doc,
            score,
            excerpt: this.highlightExcerpt(doc.content, terms),
          };
        });

      // Update metrics
      const latency = Date.now() - startTime;
      this.performanceMetrics.push(latency);
      this.queryHistory.push(query);

      // Keep only last 100 queries and metrics
      if (this.queryHistory.length > 100) {
        this.queryHistory = this.queryHistory.slice(-100);
        this.performanceMetrics = this.performanceMetrics.slice(-100);
      }

      // Update stats
      const stats = this.stats.get(projectId)!;
      stats.queryCount++;
      stats.averageLatency =
        this.performanceMetrics.reduce((sum, metric) => sum + metric, 0) /
        this.performanceMetrics.length;

      devLog.debug(`Search completed: "${query}" -> ${results.length} results in ${latency}ms`);

      return results;
    } catch (error) {
      console.error('Search failed:', error);
      return [];
    }
  }

  // Update document in search index
  async updateDocument(
    projectId: string,
    documentId: string,
    title: string,
    content: string,
  ): Promise<void> {
    await this.initializeProject(projectId);

    const index = this.indexes.get(projectId);
    const documents = this.documents.get(projectId);

    if (!index || !documents) {
      console.warn(`No index found for project ${projectId}`);
      return;
    }

    // Remove old document from index
    this.removeDocumentFromIndex(index, documentId);

    // Re-index with new content
    this.indexDocument(index, documentId, title + '\n' + content);

    // Update document store if it exists
    if (documents.has(documentId)) {
      const existing = documents.get(documentId)!;
      documents.set(documentId, {
        ...existing,
        title,
        content,
        excerpt: this.createExcerpt(content, 150),
        metadata: {
          ...existing.metadata,
          lastModified: new Date(),
        },
      });
    }

    devLog.debug(`Updated document ${documentId} in search index`);
  }

  // Get search statistics
  getStats(projectId: string): SearchStats | null {
    return this.stats.get(projectId) || null;
  }

  // Get performance metrics
  getPerformanceMetrics(): { p50: number; p95: number; queries: number } {
    if (this.performanceMetrics.length === 0) {
      return { p50: 0, p95: 0, queries: 0 };
    }

    const sorted = [...this.performanceMetrics].sort((a, b) => a - b);
    const p50Index = Math.floor(sorted.length * 0.5);
    const p95Index = Math.floor(sorted.length * 0.95);

    return {
      p50: sorted[p50Index] || 0,
      p95: sorted[p95Index] || 0,
      queries: this.performanceMetrics.length,
    };
  }

  // Private helper methods
  private tokenize(text: string): string[] {
    return text
      .toLowerCase()
      .replace(/[^\w\s]/g, ' ')
      .split(/\s+/)
      .filter((term) => term.length > 2)
      .slice(0, 20);
  }

  private indexDocument(index: Map<string, IndexEntry>, docId: string, content: string): void {
    const terms = this.tokenize(content);
    const termCounts = new Map<string, number>();

    // Count term frequencies and positions
    terms.forEach((term, position) => {
      termCounts.set(term, (termCounts.get(term) || 0) + 1);

      if (!index.has(term)) {
        index.set(term, {
          term,
          documents: new Map(),
        });
      }

      const entry = index.get(term)!;
      if (!entry.documents.has(docId)) {
        entry.documents.set(docId, {
          frequency: 0,
          positions: [],
        });
      }

      const docEntry = entry.documents.get(docId)!;
      docEntry.frequency++;
      docEntry.positions.push(position);
    });
  }

  private removeDocumentFromIndex(index: Map<string, IndexEntry>, docId: string): void {
    index.forEach((entry) => {
      entry.documents.delete(docId);
      if (entry.documents.size === 0) {
        index.delete(entry.term);
      }
    });
  }

  private createExcerpt(content: string, maxLength: number): string {
    if (content.length <= maxLength) return content;
    return content.substring(0, maxLength - 3) + '...';
  }

  private highlightExcerpt(content: string, terms: string[]): string {
    let excerpt = this.createExcerpt(content, 300);

    terms.forEach((term) => {
      const regex = new RegExp(`\\b${term}\\b`, 'gi');
      excerpt = excerpt.replace(regex, `<mark>$&</mark>`);
    });

    return excerpt;
  }

  private getAverageDocumentLength(documents: Map<string, SearchResult>): number {
    const lengths = Array.from(documents.values()).map((doc) => doc.content.length);
    return lengths.reduce((sum, len) => sum + len, 0) / lengths.length;
  }

  private calculateIndexSize(index: Map<string, IndexEntry>): number {
    let size = 0;
    index.forEach((entry) => {
      size += entry.term.length * 2; // Approximate character size
      size += entry.documents.size * 16; // Approximate document entry size
    });
    return size;
  }
}

