import type { GeneratedOutline } from './storyArchitectService';

export interface TimelineExportData {
  items: TimelineItem[];
  metadata: {
    projectId: string;
    projectName: string;
    exportDate: number;
    version: string;
  };
}

export interface TimelineImportResult {
