import devLog from "@/utils/devLogger";
// src/tour/useTourRegistration.ts - Hook to register tours on app boot

import { useEffect } from 'react';

import { registerTours, bindTourShortcut, handleTourDeepLink } from './tourLauncher';

/**
 * Hook to register all tours and set up global tour infrastructure
 * Call this once at the root of your app
 */
export function useTourRegistration() {
  useEffect(() => {
    // Register the Spotlight Tour (the only tour in the app)
    registerTours({
      spotlight: {
        start: (opts) => {
          devLog.debug('[useTourRegistration] Starting Spotlight Tour', opts);

          // Dispatch custom event that the tour provider can listen to
          const event = new CustomEvent('inkwell:start-tour', {
            detail: { tourId: 'spotlight', opts },
          });
          window.dispatchEvent(event);
        },
        reset: () => {
          devLog.debug('[useTourRegistration] Resetting Spotlight Tour');

          const event = new CustomEvent('inkwell:reset-tour', {
            detail: { tourId: 'spotlight' },
          });
          window.dispatchEvent(event);
        },
      },
    });

    // Bind keyboard shortcuts
    bindTourShortcut();

    // Handle deep links from URL
    handleTourDeepLink();

    devLog.debug('[useTourRegistration] Tour system initialized');
  }, []);
}

/**
 * Writing domain types
 * - Flexible enough to tolerate existing usage across services/components
 * - Strong where it matters (ids, nesting), loose for optional props
 */

export type SceneStatus = 'draft' | 'in_progress' | 'final' | 'archived' | (string & {});
export type ChapterStatus = 'draft' | 'revising' | 'final' | 'archived' | (string & {});

/** Export formats used by ExportDialog and downstream utilities */
export type ExportFormat = 'markdown' | 'html' | 'pdf' | 'docx' | (string & {});

/** Some code references ExportFormat without importing. Make it safe globally. */
declare global {
  var __inkwell_has_global_exportformat__: true | undefined;
  // Only define once
  // @ts-ignore
  if (!globalThis.__inkwell_has_global_exportformat__) {
    type ExportFormat = 'markdown' | 'html' | 'pdf' | 'docx' | (string & {});
    // @ts-ignore
    globalThis.__inkwell_has_global_exportformat__ = true;
  }
}

/** Common identity & audit fields */
export interface BaseEntity {
  id: string;
  createdAt?: Date | number | string;
  updatedAt?: Date | number | string;
}

/** Character (needed by timelineConflictService and tests) */
export interface Character extends BaseEntity {
  name: string;
  description?: string;
  traits?: Record<string, unknown>;
  tags?: string[];
  [key: string]: any;
}

/** Scene is the atomic writing unit */
export interface Scene extends BaseEntity {
  title: string;
  content?: string;
  /** numeric order inside its chapter (1-based or 0-based depending on caller) */
  order?: number;
  status?: SceneStatus;
  /** optional metadata used around the app */
  summary?: string;
  pov?: string;
  location?: string;
  characterIds?: string[];
  tags?: string[];
  eventType?: string; // e.g., 'plot', 'character', etc.
  importance?: 'minor' | 'major' | (string & {});
  [key: string]: any; // keep permissive for legacy usage
}

/** Chapter groups scenes */
export interface Chapter extends BaseEntity {
  title: string;
  scenes: Scene[];
  order?: number;
  status?: ChapterStatus;
  summary?: string;
  notes?: string;
  [key: string]: any;
}

// ========================================
// Enhanced Chapter Management
// ========================================

/**
 * Chapter metadata - lightweight for lists and analytics
 * Stored separately from content for performance
 */
export interface ChapterMeta {
  id: string;
  projectId: string;
  title: string;
  index: number; // display order (0-based)
  summary?: string;
  status: 'draft' | 'revising' | 'final';
  wordCount: number; // denormalized for fast UI
  sceneCount?: number; // optional: number of scenes
  tags?: string[];
  createdAt: string;
  updatedAt: string;
}

/**
 * Chapter document - heavy content stored separately
 * Enables fast lists without loading all content
 */
export interface ChapterDoc {
  id: string; // same as ChapterMeta.id
  content: string; // editor-serialized content (HTML/markdown/JSON)
  version: number; // for optimistic locking
  scenes?: Scene[]; // optional embedded scenes
}

/**
 * Full chapter with both meta and content
 * Used when editing/displaying a chapter
 */
export interface FullChapter extends ChapterMeta {
  content: string;
  version: number;
  scenes?: Scene[];
}

/**
 * Chapter creation input
 */
export interface CreateChapterInput {
  id?: string; // optional: if not provided, service will generate
  projectId: string;
  title?: string;
  summary?: string;
  content?: string;
  index?: number; // if not provided, appends to end
  status?: 'draft' | 'revising' | 'final';
}

/**
 * Chapter update input
 */
export interface UpdateChapterInput {
  id: string;
  title?: string;
  summary?: string;
  status?: 'draft' | 'revising' | 'final';
  tags?: string[];
}

/** Helpful aliases used around services */
export type WritingChapter = Chapter;
export type WritingScene = Scene;

/** Light-weight project shape some services expect when pairing with writing */
export interface WritingProject extends BaseEntity {
  name: string;
  description?: string;
  chapters?: Chapter[];
  characters?: Character[];
  currentWordCount?: number;
  sessions?: Array<{ startTime: number | string; endTime?: number | string }>;
  [key: string]: any;
}

/** Export request/response helpers */
export interface ExportRequest {
  projectId: string;
  format: ExportFormat;
  chapters?: Chapter[];
  scenes?: Scene[];
  options?: Record<string, unknown>;
}

export interface ExportResult {
  format: ExportFormat;
  blob?: Blob;
  content?: string; // for text-like exports
  filename?: string;
}

import devLog from "src/utils/devLogger";
import { FeatureFlagCategory, FeatureFlagState } from '../types/featureFlags';

import { FEATURE_FLAGS } from './featureFlags.config';

/**
 * Manages feature flag state and operations
 */
export class FeatureFlagManager {
  private static instance: FeatureFlagManager | null = null;
  private cache = new Map<string, boolean>();
  private urlParams: URLSearchParams;

  constructor() {
    this.urlParams = new URLSearchParams(
      typeof window !== 'undefined' ? window.location.search : '',
    );
    this.cache = new Map<string, boolean>();
    this.readInitialState();
  }

  static getInstance(): FeatureFlagManager {
    if (!FeatureFlagManager.instance) {
      FeatureFlagManager.instance = new FeatureFlagManager();
    }
    return FeatureFlagManager.instance;
  }

  private readInitialState(): void {
    this.cache.clear();

    // Initialize with default values
    Object.values(FEATURE_FLAGS).forEach((flag) => {
      const storageKey = `inkwell_flag_${flag.key}`;
      const storageValue = localStorage.getItem(storageKey);

      this.cache.set(flag.key, storageValue === 'true' || flag.defaultValue);
    });

    // Apply URL overrides
    this.initializeFromURL();
  }

  private initializeFromURL(): void {
    if (this.urlParams.get('trace') === '1') {
      Object.values(FEATURE_FLAGS)
        .filter((flag) => flag.category === 'debug')
        .forEach((flag) => this.cache.set(flag.key, true));
    }

    Object.values(FEATURE_FLAGS).forEach((flag) => {
      const urlValue = this.urlParams.get(flag.key);
      if (urlValue !== null) {
        this.cache.set(flag.key, urlValue === '1' || urlValue === 'true');
      }
    });
  }

  isEnabled(flagKey: string): boolean {
    if (this.cache.has(flagKey)) {
      const enabled = this.cache.get(flagKey)!;
      const flag = FEATURE_FLAGS[flagKey];

      if (flag?.dependencies?.length) {
        return enabled && flag.dependencies.every((dep) => this.isEnabled(dep));
      }

      return enabled;
    }

    const flag = FEATURE_FLAGS[flagKey];
    if (!flag) {
      console.warn(`Unknown feature flag: ${flagKey}`);
      return false;
    }

    const enabled = flag.defaultValue;
    this.cache.set(flagKey, enabled);
    return enabled;
  }

  setEnabled(flagKey: string, enabled: boolean): void {
    const flag = FEATURE_FLAGS[flagKey];
    if (!flag) {
      console.warn(`Unknown feature flag: ${flagKey}`);
      return;
    }

    const storageKey = `inkwell_flag_${flagKey}`;
    localStorage.setItem(storageKey, enabled.toString());
    this.cache.set(flagKey, enabled);

    if (flag.requiresReload) {
      devLog.debug(`âš ï¸ Feature ${flag.name} requires page reload to take effect`);
    }
  }

  reset(flagKey: string): void {
    const flag = FEATURE_FLAGS[flagKey];
    if (!flag) {
      console.warn(`Unknown feature flag: ${flagKey}`);
      return;
    }

    const storageKey = `inkwell_flag_${flagKey}`;
    localStorage.removeItem(storageKey);
    this.cache.delete(flagKey);
  }

  getAllFlags(): FeatureFlagState[] {
    return Object.values(FEATURE_FLAGS).map((flag) => ({
      ...flag,
      enabled: this.isEnabled(flag.key),
    }));
  }

  getFlagsByCategory(category: FeatureFlagCategory): FeatureFlagState[] {
    return this.getAllFlags().filter((flag) => flag.category === category);
  }

  enableDebugMode(): void {
    Object.values(FEATURE_FLAGS)
      .filter((flag) => flag.category === 'debug')
      .forEach((flag) => this.setEnabled(flag.key, true));
  }

  disableDebugMode(): void {
    Object.values(FEATURE_FLAGS)
      .filter((flag) => flag.category === 'debug')
      .forEach((flag) => this.setEnabled(flag.key, false));
  }

  resetAll(): void {
    Object.values(FEATURE_FLAGS).forEach((flag) => {
      const storageKey = `inkwell_flag_${flag.key}`;
      localStorage.removeItem(storageKey);
    });
    this.readInitialState();
  }

  exportAsURL(): string {
    const url = new URL(window.location.href);
    const modifiedFlags = this.getAllFlags().filter((flag) => flag.enabled !== flag.defaultValue);

    Object.values(FEATURE_FLAGS).forEach((flag) => {
      url.searchParams.delete(flag.key);
    });

    modifiedFlags.forEach((flag) => {
      url.searchParams.set(flag.key, flag.enabled ? '1' : '0');
    });

    return url.toString();
  }
}

import devLog from "src/utils/devLogger";
import { FeatureFlagCategory, FeatureFlagConfig, FeatureFlagState } from '../../types/featureFlags';

// Constants - this is the source of truth for test data
export const FEATURE_FLAGS: FeatureFlagConfig = {
  plotBoards: {
    key: 'plotBoards',
